mapper.update:
/*updComEvaluation*/
UPDATE
    CAM_INSIDE.DJ_EVALUATION
SET
    EVAL_CONTENT = #{content},
    EVAL_PER_CONTENT = #{perContent},
    MOD_EMP_SEQ =  #{regEmpSeq}
WHERE
    EVAL_SN = #{evalSn}

mapper.select:
SELECT A.*, B.*, C.PARENT_DEPT_SEQ, IFNULL((SELECT SUM(EVAL_SCORE + EVAL_F_SCORE + EVAL_S_SCORE) FROM CAM_INSIDE.dj_evaluation_mem WHERE EVAL_SN = #{evalSn} AND EVAL_EMP_SEQ = B.EMP_SEQ), 0)AS EVAL_TOTAL_SCORE, CASE WHEN C.DEPT_LEVEL = 2 THEN (SELECT DEPT_NAME FROM CAM_HR.DJ_DEPT_INFO DI WHERE C.PARENT_DEPT_SEQ = DI.DEPT_SEQ) ELSE B.DEPT_NAME END AS deptNm, CASE WHEN C.DEPT_LEVEL = 2 THEN (SELECT DEPT_NAME FROM CAM_HR.DJ_DEPT_INFO DI WHERE C.DEPT_SEQ = DI.DEPT_SEQ) ELSE "" END AS teamNm FROM CAM_INSIDE.dj_evaluation_mem A LEFT JOIN CAM_HR.DJ_EMP_INFO B ON A.EVAL_EMP_SEQ = B.EMP_SEQ LEFT JOIN CAM_HR.dj_dept_info C ON B.DEPT_SEQ = C.DEPT_SEQ
WHERE EVAL_SN = #{evalSn}
AND C.PARENT_DEPT_SEQ = #{dept}
AND B.DEPT_SEQ = #{team}
AND POSITION_CODE = #{position}
AND DUTY_CODE = #{duty}

/*getEvaluationMngList*/
SELECT
    *
FROM
    CAM_INSIDE.DJ_EVALUATION_ITEM
WHERE
    EVAL_SN = #{evalSn}
AND
    EVAL_TYPE = #{eType}
AND
    EVAL_POSITION_TYPE = #{pType}

/*getEvaluationScoreList*/
SELECT
    A.*,
    IFNULL(B.EVAL_SCORE, 0) AS EVAL_SCORE,
    B.TEM_ACTIVE
FROM
    CAM_INSIDE.DJ_EVALUATION_ITEM A
LEFT JOIN
    CAM_INSIDE.DJ_EVALUATION_ITEM_RESULT B ON A.EVAL_ITEM_ID = B.EVAL_ITEM_ID AND B.EMP_SEQ = #{empSeq} AND B.EVAL_EMP_SEQ = #{evalEmpSeq}
WHERE
    A.EVAL_SN = #{evalSn}

AND
    A.EVAL_TYPE = #{eType}

AND
    A.EVAL_POSITION_TYPE = #{pType}

    AND (B.EVAL_ITEM_ID IS NULL OR B.EVAL_TYPE = #{rType})

/*getEvaluationView*/
SELECT
    *
FROM
    CAM_INSIDE.DJ_EVALUATION_MEM
WHERE
    eval_sn = #{evalSn}
AND
    EVAL_EMP_SEQ = #{empSeq}

/*getEvaluationEmpList*/
SELECT row_number() OVER (ORDER BY (CASE WHEN ASCII(SUBSTRING(Z.DEPT_NAME_OD,1)) BETWEEN 48 AND 57 THEN 3 WHEN ASCII(SUBSTRING(Z.DEPT_NAME_OD,1)) < 128 THEN 2 ELSE 1 END), Z.DEPT_NAME_OD, TEAM_NAME, FIELD(DUTY_CODE, 1, 2, 3, 7, 4, 5, ''), EMP_NAME_KR) AS ROW_NUM, IF(DUTY_NAME = '' || DUTY_NAME IS NULL, POSITION_NAME, DUTY_NAME) AS DUTY_POSITION_NAME, IF(T1_A = '' || T1_A IS NULL, T1_B, T1_A) AS T1, IF(T2_A = '' || T2_A IS NULL, T2_B, T2_A) AS T2, CASE WHEN OCCUPATION_NM = 'P&M' THEN CONCAT('경영관리(', OCCUPATION_NM,')') WHEN OCCUPATION_NM = 'A&C' THEN CONCAT('기업지원(', OCCUPATION_NM,')') WHEN OCCUPATION_NM = 'R&D' THEN CONCAT('연구개발(', OCCUPATION_NM,')') ELSE '' END AS OCC_NM, Z.* FROM (SELECT IF(A.DUTY_CODE = '2', '노상흡 원장', '') AS T1_A , IF(A.DUTY_CODE = '', IFNULL( (SELECT CONCAT(EMP_NAME_KR, ' 팀장') FROM CAM_HR.DJ_EMP_INFO WHERE DEPT_SEQ = A.DEPT_SEQ AND DUTY_CODE IN ('4', '5')) , (SELECT CONCAT(EMP_NAME_KR, ' 실장') FROM CAM_HR.DJ_EMP_INFO WHERE DEPT_SEQ = (SELECT PARENT_DEPT_SEQ FROM CAM_HR.DJ_DEPT_INFO WHERE DEPT_SEQ = A.DEPT_SEQ) AND DUTY_CODE = '7')) , (SELECT CONCAT(EMP_NAME_KR, ' ', DUTY_NAME) FROM CAM_HR.DJ_EMP_INFO WHERE DEPT_SEQ = (SELECT PARENT_DEPT_SEQ FROM CAM_HR.DJ_DEPT_INFO WHERE DEPT_SEQ = A.DEPT_SEQ) AND DUTY_CODE IN ('2','3','7'))) AS T1_B , IF(A.DUTY_CODE = '5', '노상흡 원장', '') AS T2_A , IF(A.DUTY_CODE = '', (SELECT CONCAT(EMP_NAME_KR, ' ', DUTY_NAME) FROM CAM_HR.DJ_EMP_INFO WHERE DEPT_SEQ = (SELECT PARENT_DEPT_SEQ FROM CAM_HR.DJ_DEPT_INFO WHERE DEPT_SEQ = A.DEPT_SEQ) AND DUTY_CODE IN ('2','3','7')), '') AS T2_B , A.DUTY_NAME AS T2_NAME , (SELECT DEPT_NAME FROM CAM_HR.DJ_DEPT_INFO WHERE DEPT_SEQ = IF((SELECT PARENT_DEPT_SEQ FROM CAM_HR.DJ_DEPT_INFO WHERE DEPT_SEQ = A.DEPT_SEQ) = 1000, A.DEPT_SEQ, (SELECT PARENT_DEPT_SEQ FROM CAM_HR.DJ_DEPT_INFO WHERE DEPT_SEQ = A.DEPT_SEQ))) AS DEPT_NAME_OD , (SELECT CM_CODE_NM FROM CAM_COMMON.DJ_COM_CODE WHERE CM_GROUP_CODE_ID = 9 AND CM_CODE = A.OCCUPATION_CODE) AS OCCUPATION_NM , IF((SELECT PARENT_DEPT_SEQ FROM CAM_HR.DJ_DEPT_INFO WHERE DEPT_SEQ = A.DEPT_SEQ) = 1000, '', B.DEPT_NAME) AS TEAM_NAME ,(SELECT PARENT_DEPT_SEQ FROM CAM_HR.DJ_DEPT_INFO WHERE DEPT_SEQ= A.DEPT_SEQ) AS PARENT_DEPT_SEQ ,A.* ,SUBSTRING_INDEX(TRUNCATE(TIMESTAMPDIFF(month, A.JOIN_DAY, DATE_FORMAT(NOW(), '%Y-%m-%d')) / 12, 1), '.', 1) AS hire ,CONCAT(SUBSTRING_INDEX(TRUNCATE(TIMESTAMPDIFF(month, A.JOIN_DAY, DATE_FORMAT(NOW(), '%Y-%m-%d')) / 12, 1), '.', -1) + 1) AS hire_mon ,C.* FROM CAM_HR.DJ_EMP_INFO A LEFT JOIN CAM_HR.DJ_DEPT_INFO B ON A.DEPT_SEQ = B.DEPT_SEQ INNER JOIN CAM_INSIDE.DJ_EVALUATION_MEM C ON A.EMP_SEQ = C.EVAL_EMP_SEQ WHERE C.EVAL_SN = #{evalSn} AND A.ACTIVE = 'Y' AND A.WORK_STATUS_CODE = 'Y' AND DATE_FORMAT(#{bsYMD}, '%Y%m%d') >= DATE_FORMAT(A.JOIN_DAY, '%Y%m%d') AND OCCUPATION_CODE IN (1,2,3)
AND EVAL_EVAL_F_SEQ = #{empSeq} AND EVAL_EVAL_S_SEQ = #{empSeq}
) Z ORDER BY (CASE WHEN ASCII(SUBSTRING(Z.DEPT_NAME_OD,1)) BETWEEN 48 AND 57 THEN 3 WHEN ASCII(SUBSTRING(Z.DEPT_NAME_OD,1)) < 128 THEN 2 ELSE 1 END), Z.DEPT_NAME_OD, TEAM_NAME, FIELD(DUTY_CODE, 1, 2, 3, 7, 4, 5, ''), EMP_NAME_KR

/*getEvaluationEmpCount*/
SELECT COUNT(*) AS count FROM (SELECT IF(A.DUTY_CODE = '2', '노상흡 원장', '') AS T1_A , IF(A.DUTY_CODE = '', IFNULL( (SELECT CONCAT(EMP_NAME_KR, ' 팀장') FROM CAM_HR.DJ_EMP_INFO WHERE DEPT_SEQ = A.DEPT_SEQ AND DUTY_CODE IN ('4', '5')) , (SELECT CONCAT(EMP_NAME_KR, ' 실장') FROM CAM_HR.DJ_EMP_INFO WHERE DEPT_SEQ = (SELECT PARENT_DEPT_SEQ FROM CAM_HR.DJ_DEPT_INFO WHERE DEPT_SEQ = A.DEPT_SEQ) AND DUTY_CODE = '7')) , (SELECT CONCAT(EMP_NAME_KR, ' ', DUTY_NAME) FROM CAM_HR.DJ_EMP_INFO WHERE DEPT_SEQ = (SELECT PARENT_DEPT_SEQ FROM CAM_HR.DJ_DEPT_INFO WHERE DEPT_SEQ = A.DEPT_SEQ) AND DUTY_CODE IN ('2','3','7'))) AS T1_B , IF(A.DUTY_CODE = '5', '노상흡 원장', '') AS T2_A , IF(A.DUTY_CODE = '', (SELECT CONCAT(EMP_NAME_KR, ' ', DUTY_NAME) FROM CAM_HR.DJ_EMP_INFO WHERE DEPT_SEQ = (SELECT PARENT_DEPT_SEQ FROM CAM_HR.DJ_DEPT_INFO WHERE DEPT_SEQ = A.DEPT_SEQ) AND DUTY_CODE IN ('2','3','7')), '') AS T2_B , A.DUTY_NAME AS T2_NAME , (SELECT DEPT_NAME FROM CAM_HR.DJ_DEPT_INFO WHERE DEPT_SEQ = IF((SELECT PARENT_DEPT_SEQ FROM CAM_HR.DJ_DEPT_INFO WHERE DEPT_SEQ = A.DEPT_SEQ) = 1000, A.DEPT_SEQ, (SELECT PARENT_DEPT_SEQ FROM CAM_HR.DJ_DEPT_INFO WHERE DEPT_SEQ = A.DEPT_SEQ))) AS DEPT_NAME_OD , (SELECT CM_CODE_NM FROM CAM_COMMON.DJ_COM_CODE WHERE CM_GROUP_CODE_ID = 9 AND CM_CODE = A.OCCUPATION_CODE) AS OCCUPATION_NM , IF((SELECT PARENT_DEPT_SEQ FROM CAM_HR.DJ_DEPT_INFO WHERE DEPT_SEQ = A.DEPT_SEQ) = 1000, '', B.DEPT_NAME) AS TEAM_NAME ,(SELECT PARENT_DEPT_SEQ FROM CAM_HR.DJ_DEPT_INFO WHERE DEPT_SEQ= A.DEPT_SEQ) AS PARENT_DEPT_SEQ ,A.* ,SUBSTRING_INDEX(TRUNCATE(TIMESTAMPDIFF(month, A.JOIN_DAY, DATE_FORMAT(NOW(), '%Y-%m-%d')) / 12, 1), '.', 1) AS hire ,CONCAT(SUBSTRING_INDEX(TRUNCATE(TIMESTAMPDIFF(month, A.JOIN_DAY, DATE_FORMAT(NOW(), '%Y-%m-%d')) / 12, 1), '.', -1) + 1) AS hire_mon ,C.* FROM CAM_HR.DJ_EMP_INFO A LEFT JOIN CAM_HR.DJ_DEPT_INFO B ON A.DEPT_SEQ = B.DEPT_SEQ INNER JOIN CAM_INSIDE.DJ_EVALUATION_MEM C ON A.EMP_SEQ = C.EVAL_EMP_SEQ WHERE C.EVAL_SN = #{evalSn} AND A.ACTIVE = 'Y' AND A.WORK_STATUS_CODE = 'Y' AND DATE_FORMAT(#{bsYMD}, '%Y%m%d') >= DATE_FORMAT(A.JOIN_DAY, '%Y%m%d') AND OCCUPATION_CODE IN (1,2,3)
AND EVAL_EVAL_F_SEQ = #{empSeq} AND EVAL_EVAL_S_SEQ = #{empSeq}
) Z ORDER BY (CASE WHEN ASCII(SUBSTRING(Z.DEPT_NAME_OD,1)) BETWEEN 48 AND 57 THEN 3 WHEN ASCII(SUBSTRING(Z.DEPT_NAME_OD,1)) < 128 THEN 2 ELSE 1 END), Z.DEPT_NAME_OD, TEAM_NAME, FIELD(DUTY_CODE, 1, 2, 3, 7, 4, 5, ''), EMP_NAME_KR

mapper.update:
/*updEvaluationMem*/
UPDATE
    CAM_INSIDE.DJ_EVALUATION_MEM
SET


        EVAL = #{evalCk},
        EVAL_SCORE = #{totalScore},

    EVAL_VIEW = #{evalView}



        EVAL_F = #{evalCk},
        EVAL_F_SCORE = #{totalScore},

    EVAL_F_VIEW = #{evalView}



        EVAL_S = #{evalCk},
        EVAL_S_SCORE = #{totalScore},

    EVAL_S_VIEW = #{evalView}

WHERE
    EVAL_SN = #{evalSn} AND EVAL_EMP_SEQ = #{empSeq}

/*updEvaluationMemAll*/
UPDATE
    CAM_INSIDE.DJ_EVALUATION_MEM
SET

    EVAL_F = #{evalCk}


    EVAL_S = #{evalCk}

WHERE
    EVAL_SN = #{evalSn}

    AND EVAL_EVAL_F_SEQ = #{empSeq}
    AND EVAL_F = 'W'


    AND EVAL_EVAL_S_SEQ = #{empSeq}
    AND EVAL_S = 'W'

/*updEvalView*/
UPDATE
    CAM_INSIDE.DJ_EVALUATION_MEM
SET

    EVAL_F_VIEW = #{evalView}


    EVAL_S_VIEW = #{evalView}

WHERE
EVAL_SN = #{evalSn} AND EVAL_EMP_SEQ = #{empSeq}

/*setSaveMngScore*/
UPDATE
    CAM_INSIDE.DJ_EVALUATION_MEM
SET
    EVAL_SCORE_MNG = #{scoreMng}
WHERE
    EVAL_MEM_SN = #{evalMemSn}

mapper.select:
/*getEvalResultList*/ SELECT A.*, C.PARENT_DEPT_SEQ, IFNULL((SELECT SUM(EVAL_SCORE +EVAL_F_SCORE+ EVAL_S_SCORE) FROM CAM_INSIDE.dj_evaluation_mem WHERE EVAL_SN = #{evalSn} AND EVAL_EMP_SEQ = B.EMP_SEQ),0) AS EVAL_TOTAL_SCORE, CASE WHEN C.DEPT_LEVEL = 2 THEN (SELECT DEPT_NAME FROM CAM_HR.DJ_DEPT_INFO DI WHERE C.PARENT_DEPT_SEQ = DI.DEPT_SEQ) ELSE B.DEPT_NAME END AS deptNm, CASE WHEN C.DEPT_LEVEL = 2 THEN (SELECT DEPT_NAME FROM CAM_HR.DJ_DEPT_INFO DI WHERE C.DEPT_SEQ = DI.DEPT_SEQ) ELSE "" END AS teamNm, B.*, D.DEPT_MANAGER_A, D.TEAM_MANAGER_A, D.TEAM_MEMBER_A, D.DEPT_MANAGER_B, D.TEAM_MANAGER_B, D.TEAM_MEMBER_B
FROM CAM_INSIDE.dj_evaluation_mem A LEFT JOIN CAM_HR.DJ_EMP_INFO B ON A.EVAL_EMP_SEQ = B.EMP_SEQ LEFT JOIN CAM_HR.DJ_DEPT_INFO C ON B.DEPT_SEQ = C.DEPT_SEQ LEFT JOIN CAM_INSIDE.DJ_EVALUATION_CAP D ON A.EVAL_SN = D.EVAL_SN WHERE A.EVAL_SN = (SELECT EVAL_SN FROM CAM_INSIDE.DJ_EVALUATION WHERE BS_YEAR = #{searchDate} AND EVAL_NUM = #{evalNum} AND ACTIVE = 'Y' LIMIT 1)
AND C.PARENT_DEPT_SEQ = #{dept}
AND B.DEPT_SEQ = #{team}
AND B.POSITION_CODE = #{position}
AND B.DUTY_CODE = #{duty}
AND B.EMP_SEQ = #{empSeq}
