mapper.select:
/* getHistoryList */ SELECT HAI.APNT_SN, HAI.EMP_NAME, HAI.APNT_NAME, DATE_FORMAT(HAI.HISTORY_DT, '%Y-%m-%d') AS HISTORY_DT, HAI.NUMBER_NAME, IFNULL(HAI.AF_DEPT_NAME, '') AS AF_DEPT_NAME, IFNULL(HAI.AF_TEAM_NAME, '') AS AF_TEAM_NAME, IFNULL(HAI.AF_POSITION_NAME, '') AS AF_POSITION_NAME, IFNULL(HAI.AF_DUTY_NAME, '') AS AF_DUTY_NAME, HAI.ETC, AHEI.EMP_NAME_KR AS APPROVE_EMP_NAME, CFI.* FROM CAM_HR.DJ_APNT_INFO HAI LEFT JOIN CAM_HR.DJ_EMP_INFO HEI ON HAI.EMP_SEQ = HEI.EMP_SEQ LEFT JOIN CAM_HR.DJ_EMP_INFO AHEI ON HAI.REG_EMP_SEQ = AHEI.EMP_SEQ LEFT JOIN CAM_COMMON.DJ_FILE_INFO CFI ON HAI.APNT_SN = CFI.CONTENT_ID AND CFI.FILE_CD = 'history' WHERE 1=1
AND HAI.APNT_CD = #{historyType}
AND HEI.DEPT_SEQ = #{deptSeq}
AND DATE_FORMAT(HAI.HISTORY_DT, '%Y-%m-%d') BETWEEN DATE_FORMAT(#{start_date}, '%Y-%m-%d') AND DATE_FORMAT(#{end_date}, '%Y-%m-%d')
AND HEI.GENDER_CODE = #{gender}
AND HAI.ETC like CONCAT('%', #{searchText}, '%')
AND HAI.NUMBER_NAME like CONCAT('%', #{searchText}, '%')
AND HAI.EMP_NAME like CONCAT('%', #{searchText}, '%')
AND AF_POSITION_NAME like CONCAT('%', #{searchText}, '%')
AND HAI.AF_JOB_DETAIL like CONCAT('%', #{searchText}, '%')
AND HAI.AF_DUTY_NAME like CONCAT('%', #{searchText}, '%')
AND AF_POSITION_NAME like CONCAT('%', #{searchText}, '%')
AND DATE_FORMAT(HAI.HISTORY_DT, '%Y-%m') = #{applyDt}
AND HAI.EMP_SEQ = #{empSeq} AND HAI.TMP_ACTIVE = 'Y'
ORDER BY HAI.HISTORY_DT DESC

/* getHistoryListAdmin */ SELECT HAI.APNT_SN, HAI.EMP_NAME, HAI.APNT_NAME, DATE_FORMAT(HAI.HISTORY_DT, '%Y-%m-%d') AS HISTORY_DT, HAI.NUMBER_NAME, IFNULL(HAI.AF_DEPT_NAME, '') AS AF_DEPT_NAME, IFNULL(HAI.AF_TEAM_NAME, '') AS AF_TEAM_NAME, IFNULL(HAI.AF_POSITION_NAME, '') AS AF_POSITION_NAME, IFNULL(HAI.AF_DUTY_NAME, '') AS AF_DUTY_NAME, HAI.ETC, AHEI.EMP_NAME_KR AS APPROVE_EMP_NAME, CFI.* FROM CAM_HR.DJ_APNT_INFO HAI LEFT JOIN CAM_HR.DJ_EMP_INFO HEI ON HAI.EMP_SEQ = HEI.EMP_SEQ LEFT JOIN CAM_HR.DJ_EMP_INFO AHEI ON HAI.REG_EMP_SEQ = AHEI.EMP_SEQ LEFT JOIN CAM_COMMON.DJ_FILE_INFO CFI ON HAI.APNT_SN = CFI.CONTENT_ID AND CFI.FILE_CD = 'history' WHERE 1=1 AND HAI.TMP_ACTIVE = 'Y'
AND HAI.APNT_CD = #{historyType}
AND HEI.DEPT_SEQ = #{deptSeq}
AND DATE_FORMAT(HAI.HISTORY_DT, '%Y-%m-%d') BETWEEN DATE_FORMAT(#{start_date}, '%Y-%m-%d') AND DATE_FORMAT(#{end_date}, '%Y-%m-%d')
AND HEI.GENDER_CODE = #{gender}
AND HAI.ETC like CONCAT('%', #{searchText}, '%')
AND HAI.NUMBER_NAME like CONCAT('%', #{searchText}, '%')
AND HAI.EMP_NAME like CONCAT('%', #{searchText}, '%')
AND AF_POSITION_NAME like CONCAT('%', #{searchText}, '%')
AND HAI.AF_JOB_DETAIL like CONCAT('%', #{searchText}, '%')
AND HAI.AF_DUTY_NAME like CONCAT('%', #{searchText}, '%')
AND AF_POSITION_NAME like CONCAT('%', #{searchText}, '%')
AND DATE_FORMAT(HAI.HISTORY_DT, '%Y-%m') = #{applyDt}
ORDER BY HAI.APNT_SN DESC

/* getUpdHistoryList */ SELECT HAI.*, DATE_FORMAT(HAI.HISTORY_DT, '%Y-%m-%d') AS HISTORY_DATE, HEI.ERP_EMP_SEQ, HEI.EMP_NAME_KR, HAI.BF_DEPT_NAME AS DEPT_NAME, HAI.BF_TEAM_NAME AS TEAM_NAME, HAI.BF_POSITION_NAME AS POSITION_NAME, HAI.BF_DUTY_NAME AS DUTY_NAME, HAI.BF_JOB_DETAIL AS JOB_DETAIL FROM CAM_HR.DJ_APNT_INFO HAI LEFT JOIN CAM_HR.DJ_EMP_INFO HEI ON HAI.EMP_SEQ = HEI.EMP_SEQ WHERE 1=1
AND NUMBER_NAME = (SELECT SAI.NUMBER_NAME FROM CAM_HR.DJ_APNT_INFO SAI WHERE APNT_SN = #{pk})
ORDER BY HAI.APNT_SN DESC

/* getHistoryOne */ SELECT HAI.NUMBER_NAME, DATE_FORMAT(HAI.HISTORY_DT, '%Y-%m-%d') AS HISTORY_DT, HAI.APNT_NAME, HEI.ERP_EMP_SEQ, (select position_name from cam_hr.dj_emp_info where emp_seq = HAI.REG_EMP_SEQ) AS REG_POSITION_NAME, (select dept_team_name from cam_hr.dj_emp_info where emp_seq = HAI.REG_EMP_SEQ) AS REG_TEAM_NAME, HAI.REG_EMP_SEQ, HAI.EMP_NAME, HAI.REG_EMP_NAME, DATE_FORMAT(REG_DT, '%Y-%m-%d') AS REG_DT,
IFNULL(HAI.BF_DEPT_NAME, '-') AS BF_DEPT_NAME, IFNULL(HAI.BF_TEAM_NAME, '-') AS BF_TEAM_NAME, IFNULL(HAI.BF_POSITION_NAME, '-') AS BF_POSITION_NAME, IFNULL(HAI.BF_DUTY_NAME, '-') AS BF_DUTY_NAME, IFNULL(HAI.BF_JOB_DETAIL, '-') AS BF_JOB_DETAIL,
IFNULL(HAI.AF_DEPT_NAME, '-') AS AF_DEPT_NAME, IFNULL(HAI.AF_TEAM_NAME, '-') AS AF_TEAM_NAME, IFNULL(HAI.AF_POSITION_NAME, '-') AS AF_POSITION_NAME, IFNULL(HAI.AF_DUTY_NAME, '-') AS AF_DUTY_NAME, IFNULL(HAI.AF_JOB_DETAIL, '-') AS AF_JOB_DETAIL,
HAI.ETC, IFNULL(HAI.CHNG_JOB, '') AS CHNG_JOB, IFNULL(HAI.AF_JOB_DETAIL, '') AS AF_JOB_DETAIL, IFNULL(HAI.CHNG_NAME, '') AS CHNG_NAME, IFNULL(HAI.CHNG_DEPT, '') AS CHNG_DEPT, IFNULL(HAI.CHNG_POSITION, '') AS CHNG_POSITION, CFI.* FROM CAM_HR.DJ_APNT_INFO HAI LEFT JOIN CAM_HR.DJ_EMP_INFO HEI ON HAI.EMP_SEQ = HEI.EMP_SEQ LEFT JOIN CAM_COMMON.DJ_FILE_INFO CFI ON HAI.APNT_SN = CFI.CONTENT_ID AND CFI.FILE_CD = 'history' WHERE HAI.APNT_SN = #{apntSn}

/* getRewardList */ SELECT HER.REWORD_ID, HER.EMP_NAME, HER.REWORD_TYPE_NAME, (SELECT CM_CODE_NM FROM CAM_COMMON.DJ_COM_CODE WHERE CM_GROUP_CODE_ID = 32 AND CM_CODE = HER.REWORD_NAME) AS REWORD_NAME, HER.REWORD_DAY, HER.RWD_OFM, HER.RWD_ST_COMP, HER.RWD_ETC, AHEI.EMP_NAME_KR AS APPROVE_EMP_NAME, CFI.file_org_name, CFI.file_ext, CFI.file_size, IFNULL(CFI.file_no, 0) AS file_no, CFI.file_path, CFI.file_uuid, (SELECT EMP_NAME_KR FROM CAM_HR.DJ_EMP_INFO WHERE HER.REG_EMP_SEQ = EMP_SEQ) AS REG_EMP_NAME FROM CAM_HR.DJ_EMP_REWORD HER LEFT JOIN CAM_HR.DJ_EMP_INFO AHEI ON HER.EMP_SEQ = AHEI.EMP_SEQ LEFT JOIN CAM_COMMON.DJ_FILE_INFO CFI ON HER.REWORD_ID = CFI.CONTENT_ID AND CFI.FILE_CD = 'reward' WHERE DATE_FORMAT(HER.REWORD_DAY, '%Y-%m-%d') BETWEEN DATE_FORMAT(#{start_date}, '%Y-%m-%d') AND DATE_FORMAT(#{end_date}, '%Y-%m-%d') AND HER.REWARD_REGISTYN = 'N'
AND HER.REWORD_TYPE = #{rewardTypeA}
AND HER.REWORD_NAME = #{rewardName}
AND HER.DEPT_SEQ = #{deptSeq}
AND HER.TEAM_SEQ = #{teamSeq}f
AND HER.EMP_NAME like CONCAT('%', #{searchText}, '%')
AND HER.RWD_OFM like CONCAT('%', #{searchText}, '%')
AND HER.RWD_ST_COMP like CONCAT('%', #{searchText}, '%')
AND HER.RWD_SN like CONCAT('%', #{searchText}, '%')
AND HER.ACTIVE = 'Y' ORDER BY HER.REWORD_ID DESC

/* getUpdRewardList */ SELECT HER.*, AHEI.ERP_EMP_SEQ, AHEI.EMP_NAME_KR, HER.REWORD_TYPE AS REWARD_TP, HER.REWORD_NAME, HER.REWORD_DAY AS REWARD_DAY, CFI.file_org_name, CFI.file_ext, CFI.file_size, IFNULL(CFI.file_no, 0) AS file_no, CFI.file_path, CFI.file_uuid FROM CAM_HR.DJ_EMP_REWORD HER LEFT JOIN CAM_HR.DJ_EMP_INFO AHEI ON HER.EMP_SEQ = AHEI.EMP_SEQ LEFT JOIN CAM_COMMON.DJ_FILE_INFO CFI ON HER.REWORD_ID = CFI.CONTENT_ID AND CFI.FILE_CD = 'reward' WHERE 1=1
AND RWD_SN = (SELECT SER.RWD_SN FROM CAM_HR.DJ_EMP_REWORD SER WHERE SER.REWORD_ID = #{pk} AND ACTIVE = 'Y')
AND HER.ACTIVE = 'Y' ORDER BY HER.REWORD_ID DESC

mapper.update:
/*setTmpActiveUpdate*/
UPDATE CAM_HR.DJ_APNT_INFO
SET
    TMP_ACTIVE = 'Y'
WHERE
    APNT_SN = #{apntSn}

/* setInRewardAddFileNoUpdNoTmp */
UPDATE
    CAM_HR.DJ_EMP_REWORD
SET
    REWARD_ADD_NO = #{rewardAddFileNo}
WHERE
    REWORD_ID = #{rewordId}

/* appointmentEmpInfoUpd */ UPDATE CAM_HR.DJ_EMP_INFO A SET
A.DEPT_SEQ = #{afTeamSeq},
A.DEPT_NAME = #{afDeptName},
A.DEPT_TEAM_NAME = #{afTeamName},
A.POSITION_CODE = #{afPositionCode},
A.POSITION_NAME = TRIM(SUBSTRING_INDEX(#{afPositionName}, '/', 1)), A.GRADE_NAME = TRIM(SUBSTRING_INDEX(#{afPositionName}, '/', -1)),
A.DUTY_CODE = #{afDutyCode}, A.DUTY_NAME = #{afDutyName}
, A.JOB_DETAIL = #{afJobDetail}
WHERE A.EMP_SEQ = #{empSeq}
