mapper.select:
/* getHistoryList */ SELECT RE.* FROM ( SELECT @ROWNUM := @ROWNUM + 1 AS ROW_NUM, HAI.APNT_SN, HAI.EMP_NAME, HAI.APNT_NAME, DATE_FORMAT(HAI.HISTORY_DT, '%Y-%m-%d') AS HISTORY_DT, HAI.NUMBER_NAME, IFNULL(HAI.AF_DEPT_NAME, '') AS AF_DEPT_NAME, IFNULL(HAI.AF_TEAM_NAME, '') AS AF_TEAM_NAME, IFNULL(HAI.AF_POSITION_NAME, '') AS AF_POSITION_NAME, IFNULL(HAI.AF_DUTY_NAME, '') AS AF_DUTY_NAME, HAI.ETC, AHEI.EMP_NAME_KR AS APPROVE_EMP_NAME, CFI.* FROM CAM_HR.DJ_APNT_INFO HAI LEFT JOIN CAM_HR.DJ_EMP_INFO HEI ON HAI.EMP_SEQ = HEI.EMP_SEQ LEFT JOIN CAM_HR.DJ_EMP_INFO AHEI ON HAI.REG_EMP_SEQ = AHEI.EMP_SEQ LEFT JOIN CAM_COMMON.DJ_FILE_INFO CFI ON HAI.APNT_SN = CFI.CONTENT_ID AND CFI.FILE_CD = 'history', (SELECT @ROWNUM := 0) TMP WHERE 1=1
AND HAI.APNT_CD = #{historyType}
AND HEI.DEPT_SEQ = #{deptSeq}
AND DATE_FORMAT(HAI.HISTORY_DT, '%Y-%m-%d') BETWEEN DATE_FORMAT(#{start_date}, '%Y-%m-%d') AND DATE_FORMAT(#{end_date}, '%Y-%m-%d')
AND HEI.GENDER_CODE = #{gender}
AND 1 = 1
AND DATE_FORMAT(HAI.HISTORY_DT, '%Y-%m') = #{applyDt}
AND HAI.EMP_SEQ = #{empSeq}
ORDER BY HAI.REG_DT ) RE ORDER BY RE.ROW_NUM DESC

/* getHistoryOne */ SELECT HAI.NUMBER_NAME, DATE_FORMAT(HAI.HISTORY_DT, '%Y-%m-%d') AS HISTORY_DT, HEI.ERP_EMP_SEQ, HAI.EMP_NAME, HAI.REG_EMP_NAME, DATE_FORMAT(REG_DT, '%Y-%m-%d') AS REG_DT,
IFNULL(HAI.BF_DEPT_NAME, '-') AS BF_DEPT_NAME, IFNULL(HAI.BF_TEAM_NAME, '-') AS BF_TEAM_NAME, IFNULL(HAI.BF_POSITION_NAME, '-') AS BF_POSITION_NAME, IFNULL(HAI.BF_DUTY_NAME, '-') AS BF_DUTY_NAME, IFNULL(HAI.BF_JOB_DETAIL, '-') AS BF_JOB_DETAIL,
IFNULL(HAI.AF_DEPT_NAME, '-') AS AF_DEPT_NAME, IFNULL(HAI.AF_TEAM_NAME, '-') AS AF_TEAM_NAME, IFNULL(HAI.AF_POSITION_NAME, '-') AS AF_POSITION_NAME, IFNULL(HAI.AF_DUTY_NAME, '-') AS AF_DUTY_NAME, IFNULL(HAI.AF_JOB_DETAIL, '-') AS AF_JOB_DETAIL,
HAI.ETC,
CFI.* FROM CAM_HR.DJ_APNT_INFO HAI LEFT JOIN CAM_HR.DJ_EMP_INFO HEI ON HAI.EMP_SEQ = HEI.EMP_SEQ LEFT JOIN CAM_COMMON.DJ_FILE_INFO CFI ON HAI.APNT_SN = CFI.CONTENT_ID AND CFI.FILE_CD = 'history' WHERE HAI.APNT_SN = #{apntSn}

/* getRewardList */ SELECT RE.* FROM ( SELECT @ROWNUM := @ROWNUM + 1 AS ROW_NUM, HER.EMP_NAME, SUBSTRING(HER.REWORD_TYPE_NAME, 2, 4) AS SIDE_NAME, SUBSTRING(HER.REWORD_TYPE_NAME, 8) AS REWORD_TYPE_NAME, HER.REWORD_DAY, HER.RWD_OFM, HER.RWD_ST_COMP, HER.RWD_ETC, AHEI.EMP_NAME_KR AS APPROVE_EMP_NAME FROM CAM_HR.DJ_EMP_REWORD HER LEFT JOIN CAM_HR.DJ_EMP_INFO AHEI ON HER.EMP_SEQ = AHEI.EMP_SEQ, (SELECT @ROWNUM := 0) TMP WHERE DATE_FORMAT(HER.REWORD_DAY, '%Y-%m-%d') BETWEEN DATE_FORMAT(#{start_date}, '%Y-%m-%d') AND DATE_FORMAT(#{end_date}, '%Y-%m-%d')
AND HER.REWORD_TYPE_NAME like CONCAT('%', #{rewardType}, '%')
AND HER.DEPT_SEQ = #{deptSeq}
AND HER.TEAM_SEQ = #{teamSeq}
AND HER.EMP_NAME like CONCAT('%', #{searchText}, '%')
AND HER.RWD_OFM like CONCAT('%', #{searchText}, '%')
AND HER.RWD_ST_COMP like CONCAT('%', #{searchText}, '%')
AND HER.RWD_SN like CONCAT('%', #{searchText}, '%')
ORDER BY HER.REG_DATE ) RE ORDER BY RE.ROW_NUM DESC
