mapper.select:
/*getCommissionerList*/ SELECT EI.*, DI.PARENT_DEPT_SEQ, (SELECT B.CM_CODE_NM FROM CAM_COMMON.dj_com_code B WHERE B.CM_GROUP_CODE_ID = '3' AND B.CM_CODE = EI.DUTY_CODE) AS DUTY_NAME
,( SELECT COUNT(*) FROM CAM_INSIDE.DJ_APPLICATION_EVAL_LOGIN WHERE EMP_SEQ = EI.EMP_SEQ AND RECRUIT_INFO_SN = #{recruitInfoSn} AND RECRUIT_AREA_INFO_SN = #{recruitAreaInfoSn}
AND EVAL_TYPE = #{evalType}
) AS CHK
FROM CAM_HR.DJ_EMP_INFO EI LEFT JOIN CAM_HR.DJ_DEPT_INFO DI ON EI.DEPT_SEQ = DI.DEPT_SEQ WHERE 1=1 AND EI.TEMP_DIVISION = #{tempDivision} AND EI.ACTIVE = 'Y'
AND EI.DUTY_CODE IN(${dutyCode})
AND EI.LOGIN_ID LIKE CONCAT('%', #{loginId}, '%')
AND EI.EMP_NAME_KR LIKE CONCAT('%', #{empNameKr}, '%')
AND EI.DEPT_NAME LIKE CONCAT('%', #{deptName}, '%')
AND ${searchKeyWord} LIKE CONCAT('%', #{searchContent}, '%')
AND ( EI.LOGIN_ID LIKE CONCAT('%', #{searchContent}, '%') OR EI.EMP_NAME_KR LIKE CONCAT('%', #{searchContent}, '%') OR EI.DEPT_NAME LIKE CONCAT('%', #{searchContent}, '%') )
ORDER BY EI.REG_DATE DESC

mapper.update:
/* setRecruitStatusUpd */
UPDATE
    CAM_INSIDE.DJ_RECRUIT_INFO
SET
    RECRUIT_STATUS_SN = #{recruitStatusSn},
    RECRUIT_STATUS_TEXT = #{recruitStatusText}
WHERE
    RECRUIT_INFO_SN = #{recruitInfoSn}

mapper.select:
/* getRecruitAreaList */ SELECT RAI.*, CONCAT('근무부서 - [', RAI.DEPT_NAME, ' > ', RAI.TEAM_NAME, '] 채용부문 - [', RAI.JOB, '] 필요경력 - [', RAI.CAREER, ']') AS AREA_TITLE, (SELECT COUNT(*) FROM CAM_INSIDE.DJ_APPLICATION_MAIN WHERE RECRUIT_AREA_INFO_SN = RAI.RECRUIT_AREA_INFO_SN AND SAVE_TYPE = 'S') as APPLICATION_CNT, (SELECT COUNT(*) FROM CAM_INSIDE.DJ_APPLICATION_MAIN AM WHERE AM.RECRUIT_AREA_INFO_SN = RAI.RECRUIT_AREA_INFO_SN AND AM.SAVE_TYPE = 'S' AND AM.APPLICATION_STAT != 'S') AS DOC_SCREEN_CNT, (SELECT COUNT(*) FROM CAM_INSIDE.DJ_APPLICATION_MAIN AM WHERE AM.RECRUIT_AREA_INFO_SN = RAI.RECRUIT_AREA_INFO_SN AND AM.SAVE_TYPE = 'S' AND AM.APPLICATION_STAT = 'I' AND AM.IN_AVOID = 'N') AS IN_SCREEN_CNT FROM CAM_INSIDE.DJ_RECRUIT_AREA_INFO RAI
JOIN CAM_INSIDE.DJ_APPLICATION_EVAL_LOGIN EL ON EL.RECRUIT_AREA_INFO_SN = RAI.RECRUIT_AREA_INFO_SN AND EL.RECRUIT_INFO_SN = #{recruitInfoSn} AND EMP_SEQ = #{evalEmpSeq}
WHERE 1=1
AND RAI.RECRUIT_INFO_SN = #{RECRUIT_INFO_SN}
AND RAI.RECRUIT_INFO_SN = #{recruitInfoSn}
GROUP BY RAI.RECRUIT_AREA_INFO_SN ORDER BY RAI.REG_DT DESC

/* getUserInfoByApplication */
SELECT
    *
FROM
    CAM_HR.DJ_EMP_INFO
WHERE EMP_NAME_KR = #{kName}
  AND BDAY = #{bDay}
limit 1

/* getApplicationList */ SELECT AM.APPLICATION_ID, AM.USER_EMAIL, AM.RECRUIT_INFO_SN, AM.RECRUIT_AREA_INFO_SN, AM.CAREER_TYPE, AM.USER_NAME, YEAR(NOW())-LEFT(AM.BDAY ,4) +1 AS AGE, CASE WHEN AM.GENDER = 'M' THEN '남자' ELSE '여자' END AS GENDER, DAS.SCHOOL_NAME, SUM(TIMESTAMPDIFF(MONTH, WORK_ST_DT, DATE_ADD(DATE_FORMAT(WORK_EN_DT, '%Y%m%d'), INTERVAL 1 MONTH))) AS WORK_DATE, REPLACE(SUBSTRING_INDEX(REPLACE(AM.ADDR, ' ', '_'), '_', 2), '_', ' ') AS ADDR, GROUP_CONCAT(DISTINCT LA.LANG_NAME) AS LANG_NAME, AI.JOB, CASE WHEN INSTR(DATE_FORMAT(AM.SAVE_DATE, '%Y-%m-%d %p %h:%i'), 'PM') > 0 THEN REPLACE(DATE_FORMAT(AM.SAVE_DATE, '%Y-%m-%d %p %h:%i'), 'PM', '오후') ELSE REPLACE(DATE_FORMAT(AM.SAVE_DATE, '%Y-%m-%d %p %h:%i'), 'AM', '오전') END AS SAVE_DATE, (SELECT COUNT(*) FROM CAM_INSIDE.DJ_APPLICATION_MAIN WHERE SAVE_TYPE = 'S' AND AM.RECRUIT_INFO_SN != RECRUIT_INFO_SN AND AM.APPLICATION_ID != APPLICATION_ID AND AM.USER_EMAIL = USER_EMAIL) AS DUPLICATION_CNT, ( SELECT TRUNCATE(SUM(A.EVAL_ITEM_SCORE)/COUNT(DISTINCT A.EVAL_LOGIN_ID), 2) FROM CAM_INSIDE.DJ_APPLICATION_EVAL_SCORE_BOARD A JOIN CAM_INSIDE.DJ_APPLICATION_EVAL_LOGIN B ON A.EVAL_LOGIN_ID = B.EVAL_LOGIN_ID AND B.EVAL_STATUS = 'E' WHERE APPLICATION_ID = AM.APPLICATION_ID AND EVAL_ITEM_ID != 'otherRmk' AND EVAL_SCREEN_TYPE = 'doc' GROUP BY APPLICATION_ID ) AS DOC_SCREEN_AVERAGE, ( SELECT TRUNCATE(SUM(A.EVAL_ITEM_SCORE)/COUNT(DISTINCT A.EVAL_LOGIN_ID), 2) FROM CAM_INSIDE.DJ_APPLICATION_EVAL_SCORE_BOARD A LEFT JOIN CAM_INSIDE.DJ_APPLICATION_EVAL_LOGIN B ON A.EVAL_LOGIN_ID = B.EVAL_LOGIN_ID AND B.EVAL_STATUS = 'E' WHERE APPLICATION_ID = AM.APPLICATION_ID AND EVAL_ITEM_ID != 'opinion' AND EVAL_SCREEN_TYPE = 'in' GROUP BY APPLICATION_ID ) AS IN_SCREEN_AVERAGE, AM.IN_AVOID, CASE WHEN AM.IN_AVOID = 'Y' THEN '불참' ELSE '' END IN_AVOID_TXT, AM.PRELIMINARY_PASS, AM.APPLICATION_STAT FROM CAM_INSIDE.DJ_APPLICATION_MAIN AM JOIN ( SELECT DAS.APPLICATION_ID, MAX(SCHOOL_TYPE) AS SCHOOL_TYPE, CONCAT( (SELECT CONCAT(SCHOOL_NAME, ' ', DEPT) FROM CAM_INSIDE.DJ_APPLICATION_SCHOOL WHERE APPLICATION_ID = DAS.APPLICATION_ID ORDER BY SCHOOL_TYPE DESC LIMIT 1), '(', CASE WHEN MAX(SCHOOL_TYPE) = '1' THEN '고등학교' WHEN MAX(SCHOOL_TYPE) = '2' THEN '전문학사' WHEN MAX(SCHOOL_TYPE) = '3' OR MAX(SCHOOL_TYPE) = '4' THEN '학사' WHEN MAX(SCHOOL_TYPE) = '5' THEN '석사' WHEN MAX(SCHOOL_TYPE) = '6' THEN '박사' END, CASE WHEN GRADUATE_TYPE = '1' THEN '' WHEN GRADUATE_TYPE = '2' THEN ' 졸업예정' WHEN GRADUATE_TYPE = '3' THEN ' 수료' END, ')' ) AS SCHOOL_NAME FROM CAM_INSIDE.DJ_APPLICATION_SCHOOL DAS GROUP BY DAS.APPLICATION_ID )DAS ON AM.APPLICATION_ID = DAS.APPLICATION_ID LEFT JOIN CAM_INSIDE.DJ_APPLICATION_LANG_ABILITY LA ON AM.APPLICATION_ID = LA.APPLICATION_ID LEFT JOIN CAM_INSIDE.DJ_APPLICATION_CAREER AC ON AM.APPLICATION_ID = AC.APPLICATION_ID JOIN CAM_INSIDE.DJ_RECRUIT_AREA_INFO AI ON AM.RECRUIT_AREA_INFO_SN = AI.RECRUIT_AREA_INFO_SN WHERE
AM.RECRUIT_INFO_SN IN (${recruitInfoSn})
AM.RECRUIT_INFO_SN = #{recruitInfoSn}
AND AM.SAVE_TYPE = 'S'
AND AM.APPLICATION_STAT = #{searchType}
AND AM.RECRUIT_AREA_INFO_SN = #{recruitAreaInfoSn}
AND ( AM.APPLICATION_STAT = #{docScreenType} OR AM.APPLICATION_STAT = 'I' OR AM.APPLICATION_STAT = 'IF' )
AND AM.APPLICATION_STAT = #{docScreenType}
AND AM.PRELIMINARY_PASS = 'Y'
AND AM.IN_AVOID = 'Y'
AND AM.APPLICATION_STAT = #{interViewType}
GROUP BY AM.SAVE_DATE DESC ORDER BY AM.RECRUIT_INFO_SN DESC, AM.SAVE_DATE DESC

/* getInApplicationList */ SELECT AM.APPLICATION_ID, AI.RECRUIT_AREA_INFO_SN, AI.JOB, IT.IN_TIME, AM.USER_NAME, CONCAT( YEAR(NOW())-LEFT(AM.BDAY ,4) +1, '(', CASE WHEN AM.GENDER = 'M' THEN '남자' ELSE '여자' END, ')' ) AS AGE_GENDER, DAS.SCHOOL_NAME, SUM(TIMESTAMPDIFF(MONTH, WORK_ST_DT, DATE_ADD(DATE_FORMAT(WORK_EN_DT, '%Y%m%d'), INTERVAL 1 MONTH))) AS WORK_DATE, REPLACE(SUBSTRING_INDEX(REPLACE(AM.ADDR, ' ', '_'), '_', 2), '_', ' ') AS ADDR, IT.RMK
,( SELECT CASE WHEN COUNT(*) > 0 THEN 1 ELSE 0 END FROM CAM_INSIDE.DJ_APPLICATION_EVAL_SCORE_BOARD SB JOIN CAM_INSIDE.DJ_APPLICATION_EVAL_LOGIN EL ON EL.EVAL_LOGIN_ID = SB.EVAL_LOGIN_ID AND EL.EMP_SEQ = #{evalEmpSeq} WHERE AM.APPLICATION_ID = APPLICATION_ID AND SB.EVAL_SCREEN_TYPE = 'in' ) AS INSCREEN_YN
FROM CAM_INSIDE.DJ_APPLICATION_MAIN AM JOIN CAM_INSIDE.DJ_RECRUIT_AREA_INFO AI ON AM.RECRUIT_AREA_INFO_SN = AI.RECRUIT_AREA_INFO_SN
JOIN CAM_INSIDE.DJ_APPLICATION_EVAL_LOGIN EL ON EL.RECRUIT_AREA_INFO_SN = AI.RECRUIT_AREA_INFO_SN AND EVAL_LOGIN_ID = #{evalLoginId}
JOIN ( SELECT DAS.APPLICATION_ID, MAX(SCHOOL_TYPE) AS SCHOOL_TYPE, CONCAT( (SELECT CONCAT(SCHOOL_NAME, ' ', DEPT) FROM CAM_INSIDE.DJ_APPLICATION_SCHOOL WHERE APPLICATION_ID = DAS.APPLICATION_ID ORDER BY SCHOOL_TYPE DESC LIMIT 1), '(', CASE WHEN MAX(SCHOOL_TYPE) = '1' THEN '고등학교' WHEN MAX(SCHOOL_TYPE) = '2' THEN '전문학사' WHEN MAX(SCHOOL_TYPE) = '3' OR MAX(SCHOOL_TYPE) = '4' THEN '학사' WHEN MAX(SCHOOL_TYPE) = '5' THEN '석사' WHEN MAX(SCHOOL_TYPE) = '6' THEN '박사' END, CASE WHEN GRADUATE_TYPE = '1' THEN '' WHEN GRADUATE_TYPE = '2' THEN ' 졸업예정' WHEN GRADUATE_TYPE = '3' THEN ' 수료' END, ')' ) AS SCHOOL_NAME FROM CAM_INSIDE.DJ_APPLICATION_SCHOOL DAS GROUP BY DAS.APPLICATION_ID )DAS ON AM.APPLICATION_ID = DAS.APPLICATION_ID LEFT JOIN CAM_INSIDE.DJ_APPLICATION_CAREER AC ON AM.APPLICATION_ID = AC.APPLICATION_ID LEFT JOIN CAM_INSIDE.DJ_APPLICATION_IN_TIME IT ON AM.APPLICATION_ID = IT.APPLICATION_ID WHERE AM.RECRUIT_AREA_INFO_SN = #{recruitAreaInfoSn}
AND (AM.APPLICATION_STAT != 'DF' AND AM.APPLICATION_STAT != 'S')
AND AM.APPLICATION_STAT = 'D' AND AM.IN_AVOID = 'N'
AND AM.IN_AVOID != 'Y' GROUP BY AM.APPLICATION_ID ORDER BY AM.USER_NAME

/*getRecruitPrint*/ SELECT AM.APPLICATION_ID, AM.USER_EMAIL, AM.RECRUIT_INFO_SN, AM.RECRUIT_AREA_INFO_SN, AM.CAREER_TYPE, AM.USER_NAME, YEAR(NOW())-LEFT(AM.BDAY ,4) +1 AS AGE, CASE WHEN AM.GENDER = 'M' THEN '남자' ELSE '여자' END AS GENDER, DAS.SCHOOL_NAME, GROUP_CONCAT(DISTINCT AC.WORK_ST_DT, '~', AC.WORK_EN_DT SEPARATOR'|') AS WORK_DATE, REPLACE(SUBSTRING_INDEX(REPLACE(AM.ADDR, ' ', '_'), '_', 2), '_', ' ') AS ADDR, GROUP_CONCAT(DISTINCT LA.LANG_NAME,' ',LA.ACQUISITION_SCORE SEPARATOR ', ') AS LANG_NAME, AI.JOB, CASE WHEN INSTR(DATE_FORMAT(AM.SAVE_DATE, '%Y-%m-%d %p %h:%i'), 'PM') > 0 THEN REPLACE(DATE_FORMAT(AM.SAVE_DATE, '%Y-%m-%d %p %h:%i'), 'PM', '오후') ELSE REPLACE(DATE_FORMAT(AM.SAVE_DATE, '%Y-%m-%d %p %h:%i'), 'AM', '오전') END AS SAVE_DATE, (SELECT COUNT(*) FROM CAM_INSIDE.DJ_APPLICATION_MAIN WHERE SAVE_TYPE = 'S' AND AM.RECRUIT_INFO_SN != RECRUIT_INFO_SN AND AM.APPLICATION_ID != APPLICATION_ID AND AM.USER_EMAIL = USER_EMAIL) AS DUPLICATION_CNT, ( SELECT TRUNCATE(SUM(A.EVAL_ITEM_SCORE)/COUNT(DISTINCT A.EVAL_LOGIN_ID), 1) FROM CAM_INSIDE.DJ_APPLICATION_EVAL_SCORE_BOARD A JOIN CAM_INSIDE.DJ_APPLICATION_EVAL_LOGIN B ON A.EVAL_LOGIN_ID = B.EVAL_LOGIN_ID AND B.EVAL_STATUS = 'E' WHERE APPLICATION_ID = AM.APPLICATION_ID AND EVAL_ITEM_ID != 'otherRmk' AND EVAL_SCREEN_TYPE = 'doc' GROUP BY APPLICATION_ID ) AS DOC_SCREEN_AVERAGE, ( SELECT TRUNCATE(SUM(A.EVAL_ITEM_SCORE)/COUNT(DISTINCT A.EVAL_LOGIN_ID), 1) FROM CAM_INSIDE.DJ_APPLICATION_EVAL_SCORE_BOARD A LEFT JOIN CAM_INSIDE.DJ_APPLICATION_EVAL_LOGIN B ON A.EVAL_LOGIN_ID = B.EVAL_LOGIN_ID AND B.EVAL_STATUS = 'E' WHERE APPLICATION_ID = AM.APPLICATION_ID AND EVAL_ITEM_ID != 'opinion' AND EVAL_SCREEN_TYPE = 'in' GROUP BY APPLICATION_ID ) AS IN_SCREEN_AVERAGE, AM.IN_AVOID, CASE WHEN AM.IN_AVOID = 'Y' THEN '불참' ELSE '' END AS IN_AVOID_TXT, AM.PRELIMINARY_PASS, AM.APPLICATION_STAT, RI.START_DT, RI.END_DT, RI.REG_EMP_NAME, CONCAT( '근무부서 - [', AI.DEPT_NAME, ' > ', AI.TEAM_NAME, '] ', '채용부문 - [', AI.JOB, '] ', '필요경력 - [', AI.CAREER, ']' ) AS AREA_TITLE, GROUP_CONCAT(DISTINCT RAC.CERT_NAME, ' ', RAC.CERT_CLASS SEPARATOR', ') AS CERT_AREA FROM CAM_INSIDE.DJ_APPLICATION_MAIN AM JOIN ( SELECT DAS.APPLICATION_ID, MAX(SCHOOL_TYPE) AS SCHOOL_TYPE, CONCAT( (SELECT CONCAT(SCHOOL_NAME, ' ', DEPT) FROM CAM_INSIDE.DJ_APPLICATION_SCHOOL WHERE APPLICATION_ID = DAS.APPLICATION_ID ORDER BY SCHOOL_TYPE DESC LIMIT 1), '(', CASE WHEN MAX(SCHOOL_TYPE) = '1' THEN '고등학교' WHEN MAX(SCHOOL_TYPE) = '2' THEN '전문학사' WHEN MAX(SCHOOL_TYPE) = '3' OR MAX(SCHOOL_TYPE) = '4' THEN '학사' WHEN MAX(SCHOOL_TYPE) = '5' THEN '석사' WHEN MAX(SCHOOL_TYPE) = '6' THEN '박사' END, CASE WHEN GRADUATE_TYPE = '1' THEN '' WHEN GRADUATE_TYPE = '2' THEN ' 졸업예정' WHEN GRADUATE_TYPE = '3' THEN ' 수료' END, ')' ) AS SCHOOL_NAME FROM CAM_INSIDE.DJ_APPLICATION_SCHOOL DAS GROUP BY DAS.APPLICATION_ID ) DAS ON AM.APPLICATION_ID = DAS.APPLICATION_ID LEFT JOIN CAM_INSIDE.DJ_APPLICATION_LANG_ABILITY LA ON AM.APPLICATION_ID = LA.APPLICATION_ID LEFT JOIN CAM_INSIDE.DJ_APPLICATION_CAREER AC ON AM.APPLICATION_ID = AC.APPLICATION_ID LEFT JOIN CAM_INSIDE.DJ_RECRUIT_AREA_INFO AI ON AM.RECRUIT_AREA_INFO_SN = AI.RECRUIT_AREA_INFO_SN LEFT JOIN CAM_INSIDE.DJ_RECRUIT_INFO RI ON AM.RECRUIT_INFO_SN = RI.RECRUIT_INFO_SN LEFT JOIN CAM_INSIDE.DJ_APPLICATION_CERT RAC ON AM.APPLICATION_ID = RAC.APPLICATION_ID WHERE AM.RECRUIT_INFO_SN = #{recruitInfoSn} AND AM.SAVE_TYPE = 'S'
AND AM.RECRUIT_AREA_INFO_SN = #{recruitAreaInfoSn}
GROUP BY AM.APPLICATION_ID ORDER BY AM.USER_NAME

