mapper.update:
/*updBefEndDt*/
UPDATE CAM_INSIDE.DJ_SALARY_MANAGE
SET
    END_DT = #{befEndDt}
WHERE
    EMP_SEQ = #{empSeq}
AND
    END_DT = ""

mapper.select:
/* getSocialRateManageList */ SELECT A.*, CONCAT(A.START_DATE, ' ~ ', A.END_DATE) AS DT_RNG FROM CAM_INSIDE.DJ_SOCIAL_RATE A WHERE A.ACTIVE = 'Y'
AND A.SOCIAL_RATE_SN = #{socialRateSn}
ORDER BY A.END_DATE ASC
ORDER BY A.END_DATE DESC

/* getEmpNameAndTeam */ SELECT EMP_SEQ FROM CAM_HR.DJ_EMP_INFO A JOIN CAM_HR.DJ_DEPT_INFO B ON A.DEPT_SEQ = B.DEPT_SEQ WHERE A.EMP_NAME_KR = #{empName}
AND B.DEPT_NAME = '캠틱종합기술원'
AND B.DEPT_NAME = #{deptName}
