mapper.select:
/*getProjectViewList*/
SELECT A.*, left(A.FR_DT,4) AS mYEAR, CASE WHEN A.FR_DT IN ('00000000', '99991231') THEN '' ELSE CONVERT(NVARCHAR(10), CONVERT(DATETIME, A.FR_DT), 120) END AS pjtFromDate, CASE WHEN A.TO_DT IN ('00000000', '99991231') THEN '' ELSE CONVERT(NVARCHAR(10), CONVERT(DATETIME, A.TO_DT), 120) END AS pjtToDate, CONVERT(CHAR(19), A.INSERT_DT, 20) AS REG_DT FROM DZICUBE.dbo.SPJT A LEFT JOIN DZICUBE.dbo.SEMP C WITH(NOLOCK) ON (C.CO_CD = '1212' AND A.EMP_CD = C.EMP_CD) WHERE 1=1
AND A.FR_DT BETWEEN #{pjtFromDate} AND #{pjtToDate}
AND A.TO_DT BETWEEN #{pjtFromDate} AND #{pjtToDate}
AND A.PJT_CD LIKE '%' + #{searchText} + '%'
AND A.PJT_NM LIKE '%' + #{searchText} + '%'
AND C.KOR_NM LIKE '%' + #{searchText} + '%'
AND (A.PJT_CD LIKE '%' + #{searchText} + '%' OR A.PJT_NM LIKE '%' + #{searchText} + '%' OR C.KOR_NM LIKE '%' + #{searchText} + '%')
ORDER BY A.INSERT_DT DESC, FR_DT DESC

/* mssql/iCUBE/common/procedure/G20ProcedureSQL - g20Procedure.selectCommonGisuInfo */ /* 사용자 기수정보 상세 조회 */ /* 원본 프로시저 [1.0] - [dbo].[P_GWG20_COMMON_GISU_INFO] */ /* params[ erpCompSeq, baseDate(yyyyMMdd) ] */
DECLARE @CO_CD NVARCHAR(4) , @DATE NVARCHAR(8) , @FR_DT NVARCHAR(8) , @TO_DT NVARCHAR(8) , @GI_SU SMALLINT , @I SMALLINT SELECT @CO_CD = '${erpCompSeq}' , @DATE = '${baseDate}'
/************************************************************/
BEGIN SET NOCOUNT ON DECLARE @GISU INT , @GI_SU_N SMALLINT , @diff SMALLINT SELECT @GI_SU = GISU , @FR_DT = FR_DT , @TO_DT = TO_DT FROM SCO WITH(NOLOCK) WHERE CO_CD = @CO_CD
WHILE 0 < 1 BEGIN SET @i = 0 ; IF @DATE < @FR_DT SET @i = -1 ELSE IF @DATE > @TO_DT SET @i = 1 ELSE BREAK /* 현재 기수의 경우 LOOP 종료 */
/* 기수와 기수시작일 데이터 변경 */ SET @GI_SU = @GI_SU + @i SELECT @FR_DT = CONVERT(NVARCHAR(8), DATEADD(yy,@i,CONVERT(DATETIME,@FR_DT)),112) SELECT @TO_DT = CONVERT(NVARCHAR(8), DATEADD(yy,@i,CONVERT(DATETIME,@TO_DT)),112)
/* under/over flow 처리 */ IF (@GI_SU < 1) OR (@GI_SU > 19999) BREAK; END
SELECT @GI_SU_N = GISU , @FR_DT = FR_DT , @TO_DT = TO_DT FROM SCO WITH(NOLOCK) WHERE CO_CD = @CO_CD
SET @diff = @GI_SU - @GI_SU_N
SELECT @GI_SU AS gisu , CONVERT(NVARCHAR(8), DATEADD(yy,@diff,CONVERT(DATETIME,FR_DT)),112) AS fromDate , CONVERT(NVARCHAR(8), DATEADD(yy,@diff,CONVERT(DATETIME,TO_DT)),112) AS toDate FROM SCO WITH(NOLOCK) WHERE CO_CD = @CO_CD
SET NOCOUNT OFF END

/*getTradeInfo*/
SELECT
    TR_NM, REG_NB, PPL_NB, CEO_NM, BUSINESS, JONGMOK, ZIP, DIV_ADDR1, ADDR2, TEL, JIRO_NM, BA_NB
FROM ZA_TRADE_DAIKIN
WHERE TR_CD = #{TR_CD}

/*getHearnerInfo*/
SELECT
    DATA_CD, PER_CD, REG_NO, PER_NM, ZIP_CD, ADDR, PHONE, ACCT_NO, ACCT_NM
FROM VN_G20HEARNER_INFO
WHERE PER_CD = #{TR_CD}

/*getOtherList*/ SELECT PER_CD, LEFT(REG_NO,6) AS REG_NO, LEFT(REG_NO,6) + '-' + RIGHT(REG_NO,7) AS REG_NO2, PER_NM, ACCT_NO, ACCT_NM, BANK_NM FROM VN_G20HEARNER_INFO WHERE (PER_NM LIKE '%' + #{searchValue} + '%' OR REG_NO LIKE '%' + #{searchValue} + '%' OR ACCT_NO LIKE '%' + #{searchValue} + '%')
AND DATA_CD = 'G' AND REG_NO != ''
AND DATA_CD = 'F'

/*getOtherDupl*/ SELECT * FROM VN_G20HEARNER_INFO WHERE REG_NO = #{regNo}
AND DATA_CD = 'G' AND REG_NO != ''
AND DATA_CD = 'F'

/*getProjectInfo*/
SELECT
    *
FROM VN_G20SPJT_INFO AS A
WHERE PJT_CD = #{pjtCd}

/*getEtaxList*/ SELECT A.*, B.*, C.BANK_NM FROM ETAXUP A LEFT JOIN STRADE B ON A.TR_CD = B.TR_CD LEFT JOIN SBANK C ON B.JIRO_CD = C.BANK_CD WHERE ISU_DT BETWEEN #{strDt} AND #{endDt}
AND A.TAX_TY = '2'
AND A.TAX_TY = '4'
AND A.TR_NM LIKE '%' + #{searchValue} + '%'
ORDER BY ISU_DT DESC
