mapper.select:
/** getHistRndList */ SELECT A.PrjID, PrjNo, Comit, Subject, CONVERT(CHAR(10), A.PSDate, 23) AS PSDate, CONVERT(CHAR(10), A.PEDate, 23) AS PEDate, MainComit, A.UUID, MCUID, WCUID, MainMoney, SubMoney, SubMoneyU, (SELECT Sum(CAST(ISNULL(SA.NMoney,0) AS BIGINT)) AS NMoney FROM PRJDB.dbo.BudgetList SA INNER JOIN PRJDB.dbo.AccoutItem SB ON SA.ItemID = SB.ItemID WHERE SA.PrjID = A.PrjID) AS NMoney, C.name AS master, D.name AS pm, (SELECT count(-1) FROM PRJDB.dbo.UserInfo sa INNER JOIN PRJDB.dbo.PrjUser sb ON sa.UUID = sb.UUID WHERE sb.PrjID = A.PrjID) AS count FROM PRJDB.dbo.PRJINFO A LEFT JOIN PRJDB.dbo.PrjUser B ON A.PrjID = B.PrjID AND B.Type = 0 LEFT JOIN PRJDB.dbo.UserInfo C ON B.UUID = C.UUID LEFT JOIN PRJDB.dbo.UserInfo D ON A.UUID = D.UUID WHERE
YEAR(A.PSDate) <= 2023
ORDER BY A.PrjID DESC

/** getHistPurcList */
SELECT SeqNo, IsDocNo, RequestDate, (SELECT Name FROM PRJDB.dbo.UserInfo WHERE UUID = A.UUID) AS Name, IsReason, CASE WHEN IsState = '0' Then '작성중' WHEN IsState = '1'Then '요청완료' WHEN IsState = '2' Then '접수완료' ELSE '' END AS STAT, (Select Count(SeqNo) From PRJDB.dbo.JRequestItem where RequestNo = A.SeqNo AND IsState >= '1' AND IsType = '1') AS COUNT1, (Select Count(SeqNo) From PRJDB.dbo.JRequestItem where RequestNo = A.SeqNo AND IsState = '-1' AND IsType = '1') AS COUNT2, (Select Count(SeqNo) From PRJDB.dbo.JRequestItem where RequestNo = A.SeqNo AND IsState >= '1' AND IsType = '2') AS COUNT3, (Select Count(SeqNo) From PRJDB.dbo.JRequestItem where RequestNo = A.SeqNo AND IsState = '-1' AND IsType = '2') AS COUNT4 FROM PRJDB.dbo.JRequestInfo A WHERE YEAR(RequestDate) <= 2023 AND A.UUID = #{empSeq}
ORDER BY SeqNo DESC

/** getPersonAttendListG20 */
WITH DateRange(Dates) AS ( SELECT CONVERT (DATETIME, #{startDt}) --시작일자 UNION ALL SELECT DATEADD(d, 1, Dates) FROM DateRange WHERE Dates < CONVERT (DATETIME, #{endDt}) --종료일자 )
SELECT CONVERT(CHAR(10), Dates, 23) AS START_DATE, DATENAME(WEEKDAY, CONVERT(CHAR(10), Dates, 23)) AS WEEK, Name, ( Select top 1 substring(InTime,1,2) + ':' + substring(InTime,3,2) + ':' + substring(InTime,5,2) From PRJDB.dbo.CAPSData Where CapsNum = (SELECT CapsNum FROM PRJDB.dbo.USERINFO WHERE UUID = #{empSeq}) AND Convert(datetime, (replace(InDate,' ','') + ' ' + substring(InTime,1,2) + ':' + substring(InTime,3,2) + ':' + substring(InTime,5,2))) >= (Dates+' 08:00:00') AND Convert(datetime, (replace(InDate,' ','') + ' ' + substring(InTime,1,2) + ':' + substring(InTime,3,2) + ':' + substring(InTime,5,2))) <= (DATEADD(d, 1, Dates)+' 07:59:59') AND IsType = 1 ) AS START_TIME, ( Select top 1 substring(InTime,1,2) + ':' + substring(InTime,3,2) + ':' + substring(InTime,5,2) From PRJDB.dbo.CAPSData Where CapsNum = (SELECT CapsNum FROM PRJDB.dbo.USERINFO WHERE UUID = #{empSeq}) AND Convert(datetime, (replace(InDate,' ','') + ' ' + substring(InTime,1,2) + ':' + substring(InTime,3,2) + ':' + substring(InTime,5,2))) >= (Dates+' 00:00:00') AND Convert(datetime, (replace(InDate,' ','') + ' ' + substring(InTime,1,2) + ':' + substring(InTime,3,2) + ':' + substring(InTime,5,2))) <= (Dates+' 23:59:59') AND IsType = 2 ) AS END_TIME FROM DateRange LEFT JOIN PRJDB.dbo.UserInfo ON UUID = #{empSeq} OPTION (MAXRECURSION 0)

/** getOffDayListG20 */

SELECT * FROM PRJDB.dbo.UserOffDayList
WHERE OffNo IN (
    SELECT OffNo FROM PRJDB.dbo.UserOffDay
    WHERE IsState = 2
    AND UUID = #{empSeq}
)
AND SDate >= #{startDt}
AND SDate <= #{endDt}
