mapper.select:
/* getOverWorkPlanReqList */ SELECT OWP.OVER_WORK_PLAN_ID, WC.WK_CODE_NM, CONCAT(OWP.APPLY_DATE, ' ', OWP.APPLY_START_HOUR,':', OWP.APPLY_START_MIN, ' ~ ', OWP.APPLY_DATE, ' ', OWP.APPLY_END_HOUR, ':', OWP.APPLY_END_MIN, '') AS APPLY_TIME, FORMAT(OWP.APPLY_HOUR/60, 1) AS APPLY_HOUR, FORMAT(IFNULL(OWP.AGREE_HOUR, 0)/60, 1) AS AGREE_HOUR, FORMAT(IFNULL(OWP.AGREE_HOUR_NIGHT, 0)/60, 1) AS AGREE_HOUR_NIGHT, CONCAT(IFNULL(OWP.AGREE_HOUR, 0) + IFNULL(OWP.AGREE_HOUR_NIGHT, 0)) AS TOTAL_AGREE_HOUR, OWP.STATUS, DATE_FORMAT(OWP.REG_DATE, '%Y-%m-%d') AS REG_DATE, DATE_FORMAT(OWP.APPLY_DATE, '%Y-%m-%d') AS APPLY_DATE, CONCAT(DATE_FORMAT(OWP.APPLY_DATE, '%Y-%m-%d'), ' ', OWP.APPLY_START_HOUR, ':', OWP.APPLY_START_MIN) AS START_DATE, CONCAT(DATE_FORMAT(OWP.APPLY_DATE, '%Y-%m-%d'), ' ', OWP.APPLY_END_HOUR, ':', OWP.APPLY_END_MIN) AS END_DATE, OWP.APPR_STAT, VEI.EMP_NAME_KR AS EMP_NAME, VEI.DEPT_NAME, VEI.DUTY_NAME, VEI.EMP_SEQ FROM CAM_INSIDE.DJ_OVER_WORK_PLAN OWP JOIN CAM_HR.DJ_EMP_INFO VEI ON VEI.EMP_SEQ = OWP.REQUEST_EMP_SEQ JOIN DJ_CAMTIC.V_WORK_CODE WC ON WC.WK_CODE = OWP.APPLY_OVER_WORK_TYPE WHERE 1=1
AND VEI.EMP_SEQ = #{empSeq}
AND OWP.ACTIVE = 'Y'
AND DATE_FORMAT(#{startDay}, '%Y%m%d') <= DATE_FORMAT(OWP.REG_DATE, '%Y%m%d') AND DATE_FORMAT(#{endDay}, '%Y%m%d') >= DATE_FORMAT(OWP.REG_DATE, '%Y%m%d')
AND OWP.APPR_STAT = #{status}
AND OWP.VAC_USE_HIST_USE = #{vacUseHistUse}
ORDER BY OWP.REG_DATE DESC

