mapper.update:
/*updRschData*/
UPDATE CAM_PJT_MNG.DJ_PJT_RSCH
SET
    PJT_RSCH_EMP_SEQ = #{EMP_SEQ},
    PJT_RSCH_NM = #{EMP_NAME_KR}
WHERE
    PJT_SN = #{pjtSn}

mapper.select:
/*getPjtRschInfo*/
SELECT
    *
FROM
    CAM_PJT_MNG.DJ_PJT_RSCH
WHERE
    PJT_SN = #{pjtSn}

    AND PJT_RSCH_EMP_SEQ = #{rschSn}

mapper.update:
/*updRschStatus*/
UPDATE CAM_PJT_MNG.DJ_PJT_RSCH
SET REAL_YN = #{status}
WHERE
    PJT_RSCH_SN = #{pjtRschSn}

mapper.insert:
/*insDevPjtVer*/
INSERT INTO CAM_PJT_MNG.DJ_PJT_DEV
    (
        PJT_SN,
        REG_EMP_SEQ
    )
VALUES
    (
        #{pjtSn},
        #{empSeq}
    )

    SELECT AUTO_INCREMENT FROM INFORMATION_SCHEMA.TABLES WHERE TABLE_SCHEMA = 'CAM_PJT_MNG' AND TABLE_NAME = 'DJ_PJT_DEV'

mapper.update:
/*updDevInfo*/
UPDATE CAM_PJT_MNG.DJ_PJT_DEV
SET
    DEP_OBJ = #{devPlanCont},
    ETC = #{devPlanIss}
WHERE
    DEV_SN = #{devSn}

/*updDevJobInfo*/
UPDATE CAM_PJT_MNG.DJ_PJT_DEV_SCH
SET
    DEV_SCH_TITLE = #{devSchTitle},
    DEV_SCH_CONT = #{devSchCont},
    SCH_END_DE = #{schEndDe}

    , SCH_STAT = #{schStat}

WHERE
    PJT_SN = #{pjtSn}
AND
    DEV_SCH_CD = #{devSchCd}

/*updRndDetail2*/
UPDATE CAM_PJT_MNG.DJ_PJT_RND
SET
    MNG_DEPT_SEQ = #{mngDeptSeq},
    MNG_DEPT_NAME = #{mngDeptName},
    MNG_EMP_SEQ = #{mngEmpSeq},
    MNG_EMP_NAME = #{mngEmpName}
WHERE
    PJT_SN = #{pjtSn}

/*updPjtSepRnd*/
UPDATE CAM_PJT_MNG.DJ_PROJECT
SET
    SBJ_SEP = #{sbjSep}
WHERE
    PJT_SN = #{pjtSn}

/*updRndFileSn*/
UPDATE CAM_PJT_MNG.DJ_PJT_RND
SET
    RND_FILE_SN = #{file_no}
WHERE
    RND_SN = #{rndSn}

/*updRndTotResCost2*/
UPDATE CAM_PJT_MNG.DJ_PJT_RND
SET
    TOT_RES_COST = (SELECT IFNULL(SUM(CB_BUDGET), 0) AS TOTAL_AMT FROM CAM_PJT_MNG.DJ_PJT_CB
                    WHERE PJT_SN = #{pjtSn}
                      AND ACCOUNT = '0'),

    PEO_RES_COST = (SELECT IFNULL(SUM(CB_BUDGET), 0) AS TOTAL_AMT FROM CAM_PJT_MNG.DJ_PJT_CB
                    WHERE PJT_SN = #{pjtSn}
                      AND ACCOUNT = '0')
WHERE
    RND_SN = #{rndSn}

/*setDelvApprove*/
UPDATE CAM_PJT_MNG.DJ_PJT_RND
SET
    STATUS = 100
WHERE
    RND_SN = #{rndSn}

mapper.select:
/*getChangeList*/ SELECT PC.*, DI.APPRO_KEY, DI.DOC_NO, DI.DOC_MENU_CD, DI.DOC_TITLE, DI.DRAFT_EMP_NAME, DATE_FORMAT(DI.DRAFT_DT, '%Y-%m-%d') AS DRAFT_DATE FROM CAM_PJT_MNG.DJ_PJT_CHANGE PC LEFT JOIN DJ_CAMTIC.A_DOC_INFO DI ON PC.DOC_ID = DI.DOC_ID WHERE PC.PJT_SN = #{pjtSn}
ORDER BY PJT_CH_SN ASC
ORDER BY PJT_CH_SN DESC

