mapper.select:
/* getTeamList */
SELECT
    PT.*,
    DATE_FORMAT(PT.REG_DT, '%Y-%m-%d') AS REG_DATE
FROM
    CAM_PJT_MNG.DJ_PJT_TEAM PT
WHERE PJT_SN = #{pjtSn}
  AND TEAM_VERSION_SN = #{teamVersionSn}

  AND TM_TYPE = #{teamCk}


    AND ACTIVE = 'Y'

/* getTeamDetailList */ SELECT PJT_SN, PJT_NM, TM_SN, TEAM_VERSION_SN, PJT_NM, REAL_PJT_AMT, TM_TYPE, TEAM_NAME, REG_DATE, IF(TM_TYPE = '0', (RE.REAL_PJT_AMT - (SELECT SUM(SPT.TM_AMT) FROM CAM_PJT_MNG.DJ_PJT_TEAM SPT WHERE SPT.PJT_SN = 490 AND SPT.TEAM_VERSION_SN = 21 AND SPT.ACTIVE = 'Y' AND SPT.TM_TYPE != '0')), RE.TM_AMT) AS TM_AMT, TM_INV_AMT, STATUS FROM ( SELECT PT.TM_SN, PT.TEAM_VERSION_SN, P.PJT_SN, P.PJT_NM,
CASE WHEN P.BUSN_CLASS IN ('D', 'V') AND P.PJT_STEP < 'E3' THEN PD.DELV_AMT WHEN P.BUSN_CLASS IN ('D', 'V') AND P.PJT_STEP >= 'E3' THEN P.PJT_AMT WHEN P.BUSN_CLASS IN ('R', 'S') THEN P.PJT_AMT ELSE PE.EXP_AMT END AS REAL_PJT_AMT,
(SELECT SDI.DEPT_NAME FROM CAM_HR.DJ_DEPT_INFO SDI WHERE PT.TM_TEAM_SEQ = SDI.DEPT_SEQ) AS TEAM_NAME, DATE_FORMAT(PT.REG_DT, '%Y-%m-%d') AS REG_DATE, PT.TM_AMT, PT.TM_INV_AMT, PT.TM_TYPE, PTV.STATUS FROM CAM_PJT_MNG.DJ_PJT_TEAM PT LEFT JOIN CAM_PJT_MNG.DJ_PROJECT P ON P.PJT_SN = PT.PJT_SN LEFT JOIN CAM_PJT_MNG.DJ_PJT_DELV PD ON PD.PJT_SN = P.PJT_SN LEFT JOIN CAM_PJT_MNG.DJ_PJT_ENGN PE ON PE.PJT_SN = P.PJT_SN LEFT JOIN CAM_PJT_MNG.DJ_PJT_TEAM_VERSION PTV ON PT.TEAM_VERSION_SN = PTV.TEAM_VERSION_SN WHERE PT.PJT_SN = #{pjtSn} AND PT.TEAM_VERSION_SN = #{teamVersionSn} AND PT.ACTIVE = 'Y' ) RE

/* getTeamBudgetList */
SELECT
    *
FROM
    CAM_PJT_MNG.DJ_PJT_TEAM_BUDGET
WHERE TM_SN = #{tmSn}

/* getVerLeftAmt */
SELECT
    IFNULL(SUM(TM_AMT), 0) AS TM_AMT_SUM
FROM
    CAM_PJT_MNG.DJ_PJT_TEAM
WHERE PJT_SN = #{pjtSn}
  AND TEAM_VERSION_SN = #{teamVersionSn}
  AND TM_TYPE = '1'
  AND ACTIVE = 'Y'

/* getTeamData */
SELECT
    *
FROM
    CAM_PJT_MNG.DJ_PJT_TEAM PT
WHERE TM_SN = #{tmSn}

mapper.update:
/* updMyTeam */
UPDATE CAM_PJT_MNG.DJ_PJT_TEAM
SET
    TM_INV_AMT = #{myInvAmt}
WHERE TEAM_VERSION_SN = #{teamVersionSn}
  AND PJT_SN = #{pjtSn}
  AND TM_TYPE = #{tmType}

/* updTeam */
UPDATE CAM_PJT_MNG.DJ_PJT_TEAM
SET
    TM_AMT = #{teamAmt},
    TM_INV_AMT = #{tmInvAmt},
    MOD_DT = NOW(),
    MOD_EMP_SEQ = #{regEmpSeq}
WHERE
    TM_SN = #{tmSn}

/* updPmAppStat */
UPDATE CAM_PJT_MNG.DJ_PJT_TEAM
SET
    PM_CK = 'Y'
WHERE TEAM_VERSION_SN = #{teamVersionSn}
  AND PM_EMP_SEQ = #{regEmpSeq}
  AND PM_EMP_SEQ IS NOT NULL

/* updTeamAppStat */
UPDATE CAM_PJT_MNG.DJ_PJT_TEAM
SET
    TEAM_CK = 'Y'
WHERE TEAM_VERSION_SN = #{teamVersionSn}
  AND TEAM_EMP_SEQ = #{regEmpSeq}
  AND TEAM_EMP_SEQ IS NOT NULL

/* updTeamVersionAppStat */
UPDATE CAM_PJT_MNG.DJ_PJT_TEAM_VERSION
SET
    STATUS = #{stat},
    APPROVAL_DATE = NOW()
WHERE TEAM_VERSION_SN = #{teamVersionSn}
  AND PJT_SN = #{pjtSn}
