mapper.select:
/* getTeamList */
SELECT
    PT.*,
    DATE_FORMAT(PT.REG_DT, '%Y-%m-%d') AS REG_DATE
FROM
    CAM_PJT_MNG.DJ_PJT_TEAM PT
WHERE PJT_SN = #{pjtSn}
  AND TEAM_VERSION_SN = #{teamVersionSn}

  AND TM_TYPE = #{teamCk}


    AND ACTIVE = 'Y'

/* getVerLeftAmt */
SELECT
    IFNULL(SUM(TM_AMT), 0) AS TM_AMT_SUM
FROM
    CAM_PJT_MNG.DJ_PJT_TEAM
WHERE PJT_SN = #{pjtSn}
  AND TEAM_VERSION_SN = #{teamVersionSn}
  AND TM_TYPE = '1'
  AND ACTIVE = 'Y'

/* getTeamData */
SELECT
    *
FROM
    CAM_PJT_MNG.DJ_PJT_TEAM PT
WHERE TM_SN = #{tmSn}

mapper.update:
/* updMyTeam */
UPDATE CAM_PJT_MNG.DJ_PJT_TEAM
SET
    TM_INV_AMT = #{myInvAmt}
WHERE TEAM_VERSION_SN = #{teamVersionSn}
  AND PJT_SN = #{pjtSn}
  AND TM_TYPE = #{tmType}

mapper.insert:
/* updTeam */
UPDATE CAM_PJT_MNG.DJ_PJT_TEAM
SET
    TM_AMT = #{teamAmt},
    TM_INV_AMT = #{tmInvAmt},
    MOD_DT = NOW(),
    MOD_EMP_SEQ = #{regEmpSeq}
WHERE
    TM_SN = #{tmSn}

mapper.update:
/* delTeamProject */
UPDATE CAM_PJT_MNG.DJ_PROJECT
SET
    PJT_STOP = 'Y',
    ACTIVE = 'N'
WHERE TM_SN = #{TM_SN}

/* updTeamVersionAppStat */
UPDATE CAM_PJT_MNG.DJ_PJT_TEAM_VERSION
SET
    STATUS = #{stat}
WHERE TEAM_VERSION_SN = #{teamVersionSn}
  AND PJT_SN = #{pjtSn}
