mapper.update:
/*updUnRndFileSn*/
UPDATE CAM_PJT_MNG.DJ_PJT_UNRND
SET
    UN_RND_FILE_SN = #{file_no}
WHERE
    UN_RND_SN = #{unRndSn}

/*setDelvApprove*/
UPDATE CAM_PJT_MNG.DJ_PJT_UNRND
SET
    STATUS = 100
WHERE
    PJT_SN = #{pjtSn}

/*updUnRndProjectInfo*/
UPDATE CAM_PJT_MNG.DJ_PROJECT
SET
    PJT_STEP = "S2",
    PJT_STEP_NM = "수주보고",
    PJT_AMT = #{pjtExpAmt}
WHERE
    PJT_SN = #{pjtSn}

mapper.select:
/*getPersonList*/ SELECT * FROM CAM_PJT_MNG.DJ_PJT_LECTURE_PERSON WHERE 1=1
AND PERSON_SN NOT IN ( SELECT PERSON_SN FROM CAM_PJT_MNG.DJ_PJT_LECTURE_PERSON_REQ WHERE PERSON_SN = #{notIn} AND ACTIVE = 'Y' )
ORDER BY NAME

/*getLectureTeacherList*/ SELECT * FROM CAM_PJT_MNG.DJ_PJT_LECTURE_TEACHER WHERE 1=1
AND TEACHER_SN NOT IN ( SELECT TEACHER_SN FROM CAM_PJT_MNG.DJ_PJT_LECTURE_TEACHER_REQ WHERE LEC_SN = #{notIn} )
ORDER BY NAME

mapper.insert:
/*insLectureTeacherInfo*/
INSERT INTO CAM_PJT_MNG.DJ_PJT_LECTURE_TEACHER_REQ
    (
        LEC_SN,
        TEACHER_SN
    )
VALUES

    (
        #{pk},
        #{item.value}
    )

/*insLecturePersonInfo*/
INSERT INTO CAM_PJT_MNG.DJ_PJT_LECTURE_PERSON_REQ
    (
        LEC_SN,
        PERSON_SN
    )
VALUES

    (
        #{pk},
        #{item.value}
    )

mapper.update:
/*updPersonApp*/
UPDATE
    CAM_PJT_MNG.DJ_PJT_LECTURE_PERSON_REQ
SET
    REQ_STATUS = #{stat},
    REQ_STATUS_NAME = #{statText}
WHERE
    LEC_SN = #{pk}
AND PERSON_SN = (${personList})

/*updPersonPartic*/
UPDATE
    CAM_PJT_MNG.DJ_PJT_LECTURE_PERSON_REQ
SET
    PARTIC_YN = #{stat}
WHERE
    LEC_SN = #{pk}
AND PERSON_SN = (${personList})

/*updPersonRefund*/
UPDATE
    CAM_PJT_MNG.DJ_PJT_LECTURE_PERSON_REQ
SET
    REFUND_TYPE = #{stat}

        , COST_YN = #{stat}

WHERE
    LEC_SN = #{pk}
AND PERSON_SN = (${personList})
