mapper.select:
/*getPartRateVersionList*/ SELECT A.*, B.PJT_NM, DATE_FORMAT(B.STR_DT, '%Y-%m-%d') AS STR_DT, DATE_FORMAT(B.END_DT, '%Y-%m-%d') AS END_DT, DATE_FORMAT(A.REQ_DATE, '%Y-%m-%d') AS REQ_DT, B.BUSN_NM, DI.APPRO_KEY, DI.DOC_MENU_CD FROM CAM_PJT_MNG.DJ_PJT_PART_RATE_VER A LEFT JOIN CAM_PJT_MNG.DJ_PROJECT B ON A.PJT_SN = B.PJT_SN LEFT JOIN DJ_CAMTIC.A_DOC_INFO DI ON A.DOC_ID = DI.DOC_ID WHERE 1 = 1
AND B.BUSN_CLASS = #{busnClass}
AND PJT_NM LIKE CONCAT('%', #{pjtNm}, '%')
ORDER BY PART_RATE_VER_SN DESC

