/*getProjectTotalData*/ WITH RECURSIVE PROJECT AS ( SELECT #{busnClass} AS BUSN_CLASS ) SELECT (SELECT COUNT(-1) FROM CAM_PJT_MNG.DJ_PROJECT SP WHERE PJT_STEP IN ('E', 'E1', 'E2') AND SP.PJT_STOP = 'N' AND TEAM_STAT = 'N') AS ENGN_EXPECT_COUNT, (SELECT IFNULL(SUM(SE.EXP_AMT), 0) FROM CAM_PJT_MNG.DJ_PROJECT SP LEFT JOIN CAM_PJT_MNG.DJ_PJT_ENGN SE ON SP.PJT_SN = SE.PJT_SN WHERE PJT_STEP IN ('E', 'E1', 'E2') AND SP.PJT_STOP = 'N' AND TEAM_STAT = 'N') AS ENGN_EXPECT_SUM, (SELECT COUNT(-1) FROM CAM_PJT_MNG.DJ_PROJECT SP WHERE PJT_STEP IN ('E3', 'E4', 'E5') AND SP.PJT_STOP = 'N' AND TEAM_STAT = 'N') AS ENGN_PROGRESS_COUNT, (SELECT IFNULL(SUM(SP.PJT_AMT), 0) FROM CAM_PJT_MNG.DJ_PROJECT SP WHERE PJT_STEP IN ('E3', 'E4', 'E5') AND SP.PJT_STOP = 'N' AND TEAM_STAT = 'N') AS ENGN_PROGRESS_SUM, (SELECT COUNT(-1) FROM CAM_PJT_MNG.DJ_PROJECT SP WHERE PJT_STEP IN ('E6', 'E7') AND SP.PJT_STOP = 'N' AND TEAM_STAT = 'N') AS ENGN_COMPLETE_COUNT, (SELECT IFNULL(SUM(SP.PJT_AMT), 0) FROM CAM_PJT_MNG.DJ_PROJECT SP WHERE PJT_STEP IN ('E6', 'E7') AND SP.PJT_STOP = 'N' AND TEAM_STAT = 'N') AS ENGN_COMPLETE_SUM,
(SELECT COUNT(-1) FROM CAM_PJT_MNG.DJ_PROJECT SP WHERE PJT_STEP IN ('R') AND SP.PJT_STOP = 'N' AND TEAM_STAT = 'N') AS RND_EXPECT_COUNT, (SELECT IFNULL(SUM(SP.PJT_EXP_AMT), 0) FROM CAM_PJT_MNG.DJ_PROJECT SP WHERE PJT_STEP IN ('R') AND SP.PJT_STOP = 'N' AND TEAM_STAT = 'N') AS RND_EXPECT_SUM, (SELECT COUNT(-1) FROM CAM_PJT_MNG.DJ_PROJECT SP WHERE PJT_STEP IN ('R2') AND SP.PJT_STOP = 'N' AND TEAM_STAT = 'N') AS RND_PROGRESS_COUNT, (SELECT IFNULL(SUM(SP.PJT_AMT), 0) FROM CAM_PJT_MNG.DJ_PROJECT SP WHERE PJT_STEP IN ('R2') AND SP.PJT_STOP = 'N' AND TEAM_STAT = 'N') AS RND_PROGRESS_SUM, (SELECT COUNT(-1) FROM CAM_PJT_MNG.DJ_PROJECT SP WHERE PJT_STEP IN ('R3') AND SP.PJT_STOP = 'N' AND TEAM_STAT = 'N') AS RND_COMPLETE_COUNT, (SELECT IFNULL(SUM(SP.PJT_AMT), 0) FROM CAM_PJT_MNG.DJ_PROJECT SP WHERE PJT_STEP IN ('R3') AND SP.PJT_STOP = 'N' AND TEAM_STAT = 'N') AS RND_COMPLETE_SUM,
(SELECT COUNT(-1) FROM CAM_PJT_MNG.DJ_PROJECT SP WHERE PJT_STEP IN ('S') AND SP.PJT_STOP = 'N' AND TEAM_STAT = 'N') AS URND_EXPECT_COUNT, (SELECT IFNULL(SUM(SP.PJT_EXP_AMT), 0) FROM CAM_PJT_MNG.DJ_PROJECT SP WHERE PJT_STEP IN ('S') AND SP.PJT_STOP = 'N' AND TEAM_STAT = 'N') AS URND_EXPECT_SUM, (SELECT COUNT(-1) FROM CAM_PJT_MNG.DJ_PROJECT SP WHERE PJT_STEP IN ('S2') AND SP.PJT_STOP = 'N' AND TEAM_STAT = 'N') AS URND_PROGRESS_COUNT, (SELECT IFNULL(SUM(SP.PJT_AMT), 0) FROM CAM_PJT_MNG.DJ_PROJECT SP WHERE PJT_STEP IN ('S2') AND SP.PJT_STOP = 'N' AND TEAM_STAT = 'N') AS URND_PROGRESS_SUM, (SELECT COUNT(-1) FROM CAM_PJT_MNG.DJ_PROJECT SP WHERE PJT_STEP IN ('S3') AND SP.PJT_STOP = 'N' AND TEAM_STAT = 'N') AS URND_COMPLETE_COUNT, (SELECT IFNULL(SUM(SP.PJT_AMT), 0) FROM CAM_PJT_MNG.DJ_PROJECT SP WHERE PJT_STEP IN ('S3') AND SP.PJT_STOP = 'N' AND TEAM_STAT = 'N') AS URND_COMPLETE_SUM FROM PROJECT P

