mapper.select:
/* getVacCodeList */
SELECT
    *
FROM
    CAM_INSIDE.DJ_SUBHOLIDAY_CODE
WHERE
    ACTIVE = "Y"
AND
    SUBHOLIDAY_MC_CODE = #{mcCode}

        AND SUBHOLIDAY_MD_CODE = #{mdCode}


        AND SUBHOLIDAY_DT_CODE = #{dtCode}


        AND SUBHOLIDAY_CODE_ID != '9'

/* getVacUseHistoryList */ SELECT RE.* FROM ( SELECT @ROWNUM := @ROWNUM + 1 AS ROW_NUM, VUH.*, VCD.SUBHOLIDAY_DT_CODE_NM, DI.APPRO_KEY, DI.DOC_MENU_CD FROM CAM_INSIDE.DJ_SUBHOLIDAY_USE_HIST VUH JOIN CAM_INSIDE.DJ_SUBHOLIDAY_CODE VCD ON VUH.SUBHOLIDAY_CODE_ID = VCD.SUBHOLIDAY_CODE_ID LEFT JOIN DJ_CAMTIC.A_DOC_INFO DI ON VUH.DOC_ID = DI.DOC_ID, (SELECT @ROWNUM := 0) TMP WHERE 1=1 AND VUH.REG_EMP_SEQ = #{empSeq}
AND VUH.APPR_STAT = #{status}
AND VUH.SUBHOLIDAY_CODE_ID = #{edtHolidayKindTop}
ORDER BY VUH.APPLY_DATE ) RE ORDER BY RE.ROW_NUM DESC

mapper.insert:
/* setVacUseHist */
INSERT INTO CAM_INSIDE.DJ_SUBHOLIDAY_USE_HIST ( SUBHOLIDAY_CODE_ID, APPLY_SEQ, APPLY_DATE, SAVE_SEQ, SUBHOLIDAY_ST_DT, SUBHOLIDAY_ST_TIME, SUBHOLIDAY_EN_DT, SUBHOLIDAY_EN_TIME, RMK, SUBHOLIDAY_TARGET_SEQ, RMK_OTHER, REG_EMP_SEQ, SUBHOLIDAY_WORK_DAY, SUBHOLIDAY_ALTERNATIVE_DAY, SUBHOLIDAY_USE_DAY
, APPROVAL_REASON
) VALUES ( #{vacCodeId}, #{applySeq}, #{applyDate}, #{saveSeq}, #{vacUseStDt}, #{vacUseStTime}, #{vacUseEndt}, #{vacUseEnTime}, #{rmk}, #{vacTargetSeq}, #{rmkOther}, #{saveSeq}, #{vacWorkDt}, #{vacUseAlDt}, #{useDay}
, #{approvalReason}
)
SELECT AUTO_INCREMENT FROM INFORMATION_SCHEMA.TABLES WHERE TABLE_SCHEMA = 'CAM_INSIDE' AND TABLE_NAME = 'DJ_SUBHOLIDAY_USE_HIST'

mapper.update:
UPDATE CAM_INSIDE.DJ_SUBHOLIDAY_USE_HIST SET SUBHOLIDAY_CODE_ID = #{vacCodeId}, SUBHOLIDAY_ST_DT = #{vacUseStDt}, SUBHOLIDAY_ST_TIME = #{vacUseStTime}, SUBHOLIDAY_EN_DT = #{vacUseEndt}, SUBHOLIDAY_EN_TIME = #{vacUseEnTime}, SUBHOLIDAY_USE_DAY = #{useDay}, SUBHOLIDAY_WORK_DAY = #{vacWorkDt}, SUBHOLIDAY_ALTERNATIVE_DAY = #{vacUseAlDt}, RMK = #{rmk}, RMK_OTHER = #{rmkOther}, MOD_DATE = NOW(), MOD_EMP_SEQ = #{saveSeq}
, APPROVAL_REASON = #{approvalReason}
WHERE SUBHOLIDAY_USE_ID = #{vacUseHistId}

UPDATE COM_INSIDE.DJ_OVER_WORK_PLAN SET VAC_USE_HIST_USE = #{vacUseHistUse}, VAC_USE_HIST_USE_ID = #{vacUseHistId}, MOD_DATE = NOW() WHERE OVER_WORK_PLAN_ID IN (
#{item}
)

mapper.select:
/* getVacUseHistoryListAdmin */ SELECT VUH.*, DEI.EMP_NAME_KR, VCD.SUBHOLIDAY_DT_CODE_NM, DI.DOC_ID, DI.APPRO_KEY, DI.DOC_MENU_CD FROM CAM_INSIDE.DJ_SUBHOLIDAY_USE_HIST VUH JOIN CAM_INSIDE.DJ_SUBHOLIDAY_CODE VCD ON VUH.SUBHOLIDAY_CODE_ID = VCD.SUBHOLIDAY_CODE_ID LEFT JOIN CAM_HR.DJ_EMP_INFO DEI ON VUH.REG_EMP_SEQ = DEI.EMP_SEQ LEFT JOIN DJ_CAMTIC.A_DOC_INFO DI ON VUH.DOC_ID = DI.DOC_ID where 1=1
AND VUH.APPR_STAT = #{status}
AND VUH.SUBHOLIDAY_CODE_ID = #{edtHolidayKindTop}
AND DEI.EMP_NAME_KR LIKE CONCAT('%',#{searchVal},'%')

/* getUserVacList */ SELECT VUI.EMP_SEQ, VUI.EMP_NAME_KR, VUI.DEPT_NAME, VUI.POSITION_NAME, VUI.DEPT_TEAM_NAME, VUI.DUTY_NAME, DATE_FORMAT(VUI.JOIN_DAY, '%Y-%m-%d') AS JOIN_DAY, VUI.ACTIVE, VC.APPLY_YEAR, CONCAT(VC.APPLY_YEAR, '-01-01') AS ST_APPLY_DATE, CONCAT(VC.APPLY_YEAR, '-12-31') AS EN_APPLY_DATE, VC.GRANT_DAY, VC.MDTN_DAY, VC.EXTRA_DAY, VC.USE_DAY, VC.CARRYOVER_DAY, VC.ADJUSTMENT_USE_DAY, VC.COMP_VAC, ((VC.GRANT_DAY + VC.MDTN_DAY + VC.EXTRA_DAY) - (VC.USE_DAY)) AS REMAIN_VAC, VC.ACTIVE, VC.HOLIDAY_ID, IFNULL((SELECT USE_DAY FROM CAM_INSIDE.DJ_HOLIDAY WHERE APPLY_YEAR = #{befYear} AND EMP_SEQ = VUI.EMP_SEQ), 0) AS befUseDay, IFNULL((SELECT USE_DAY FROM CAM_INSIDE.DJ_HOLIDAY WHERE APPLY_YEAR = #{bef2Year} AND EMP_SEQ = VUI.EMP_SEQ), 0) AS bef2UseDay FROM CAM_HR.DJ_EMP_INFO VUI LEFT JOIN CAM_INSIDE.DJ_HOLIDAY VC ON VUI.EMP_SEQ = VC.EMP_SEQ AND VC.APPLY_YEAR = #{holidayYear} WHERE VUI.ACTIVE = "Y"
AND VUI.DEPT_SEQ = #{deptSeq}
AND VUI.EMP_NAME_KR LIKE CONCAT('%',#{searchVal},'%')
AND VC.APPLY_YEAR = DATE_FORMAT(NOW(), '%Y') AND VC.ACTIVE = #{vacStatus} AND VC.APPLY_YEAR = DATE_FORMAT(NOW(), '%Y') AND VC.ACTIVE = #{vacStatus} AND VC.APPLY_YEAR IS NULL

/* getUserVacListStat */ /*1(연차), 3(오전반차), 4(오후반차), 5(병가), 6(공가), 7(경조휴가), 8(출산휴가), 9(대체휴가), 10(근속포상휴가), 11(휴일근로)*/
SELECT VUI.emp_seq, VUI.emp_name_kr, VUI.dept_name, VUI.position_name, VUI.dept_team_name, VUI.duty_name, Date_format(VUI.join_day, '%Y-%m-%d') AS JOIN_DAY, VUI.active, VC.apply_year, Concat(VC.apply_year, '-01-01') AS ST_APPLY_DATE, Concat(VC.apply_year, '-12-31') AS EN_APPLY_DATE, VC.grant_day, VC.mdtn_day, VC.extra_day, VC.use_day, ( ( VC.grant_day + VC.mdtn_day + VC.extra_day ) - ( VC.use_day ) ) AS REMAIN_VAC, VC.active, VC.holiday_id, Sum(CASE WHEN VUH.subholiday_code_id = 1 THEN 1 ELSE 0 END) AS ANNUAL, Sum(CASE WHEN VUH.subholiday_code_id = 3 THEN 1 ELSE 0 END) AS MORNING, Sum(CASE WHEN VUH.subholiday_code_id = 4 THEN 1 ELSE 0 END) AS AFTERNOON, Sum(CASE WHEN VUH.subholiday_code_id = 5 THEN 1 ELSE 0 END) AS SICK, Sum(CASE WHEN VUH.subholiday_code_id = 6 THEN 1 ELSE 0 END) AS PUBLICHOLI, Sum(CASE WHEN VUH.subholiday_code_id = 7 THEN 1 ELSE 0 END) AS CONDOLENCES, Sum(CASE WHEN VUH.subholiday_code_id = 8 THEN 1 ELSE 0 END) AS MATERNITY, Sum(CASE WHEN VUH.subholiday_code_id = 9 THEN 1 ELSE 0 END) AS ALTERNATIVE, Sum(CASE WHEN VUH.subholiday_code_id = 10 THEN 1 ELSE 0 END) AS LONGAWARD, Sum(CASE WHEN VUH.subholiday_code_id = 11 THEN 1 ELSE 0 END) AS HOLIDAYWORK FROM cam_inside.dj_holiday VC LEFT JOIN cam_hr.dj_emp_info VUI ON VC.emp_seq = VUI.emp_seq LEFT JOIN cam_inside.dj_subholiday_use_hist VUH ON VUI.emp_seq = VUH.reg_emp_seq WHERE VUH.subholiday_code_id IN ( 1, 3, 4, 5, 6, 7, 8, 9, 10, 11 )
AND VC.APPLY_YEAR = #{holidayYear}
AND VUI.DEPT_NAME = #{deptName}
AND VUI.DEPT_NAME = #{deptTeamName}
AND VUH.SUBHOLIDAY_CODE_ID = #{edtHolidayKindTop}
AND VUI.EMP_NAME_KR LIKE CONCAT('%',#{searchVal},'%')
GROUP BY VUI.emp_seq, VUI.emp_name_kr, VUI.dept_name, VUI.position_name, VUI.duty_name, Date_format(VUI.join_day, '%Y-%m-%d'), VUI.active, VC.apply_year, Concat(VC.apply_year, '-01-01'), Concat(VC.apply_year, '-12-31'), VC.grant_day, VC.mdtn_day, VC.extra_day, VC.use_day, ( ( VC.grant_day + VC.mdtn_day + VC.extra_day ) - ( VC.use_day ) ), VC.active, VC.holiday_id

mapper.update:
/* updateApprStat */
UPDATE
    CAM_INSIDE.DJ_SUBHOLIDAY_USE_HIST
SET
    APPR_STAT = #{status},
    APPROVAL_SEND_DATE = DATE_FORMAT(NOW(), '%Y-%m-%d'),
    APPROVAL_EMP_SEQ = #{empSeq},
    DOC_ID = #{docId}
WHERE
    SUBHOLIDAY_USE_ID = #{subholidayUseId}

/* updateApprFinalStat */
UPDATE
    CAM_INSIDE.DJ_SUBHOLIDAY_USE_HIST
SET
    APPR_STAT = #{status}
WHERE
    SUBHOLIDAY_USE_ID = #{subholidayUseId}

mapper.select:
/* getUserHolyData*/
SELECT
    *
FROM
    CAM_INSIDE.DJ_HOLIDAY DH
WHERE
    DH.ACTIVE = "Y" AND DH.APPLY_YEAR = #{year}
AND
    EMP_SEQ = #{empSeq}

update.foreach:
UPDATE CAM_INSIDE.DJ_HOLIDAY
SET
    GRANT_DAY = #{item.GRANT_DAY},
    COMP_VAC = #{item.COMP_VAC}
WHERE
    HOLIDAY_ID = #{item.HOLIDAY_ID}
