mapper.select:
/* getMenuList */ SELECT GROUP_ID, UPPER_GROUP_ID, GROUP_NAME, ACTIVE, GROUP_NAME AS TEXT, GROUP_CHILDREN_YN FROM CAM_HR.DJ_TEXTMESSAGES_GROUP WHERE 1 = 1
AND GROUP_ID NOT IN (#{groupId})
AND REG_EMP_SEQ = #{loginEmpSeq}

/* getMailDetList */ SELECT CMHD.*, CMH.MAIL_TILE FROM CAM_COMMON.DJ_MAIL_HIST_DET CMHD LEFT JOIN CAM_COMMON.DJ_MAIL_HIST CMH ON CMHD.MAIL_HIST_SN = CMH.MAIL_HIST_SN WHERE CMHD.MAIL_HIST_SN = #{mailHistSn} AND CMHD.ACTIVE = 'Y'
AND MAIL_HIST_DET_SN IN (${mailArr})
AND SEND_YN = 'N'

mapper.insert:
/* setGroup */
INSERT INTO CAM_HR.DJ_TEXTMESSAGES_GROUP
    (
        GROUP_NAME,
        REG_EMP_SEQ
    )
VALUES
    (
        #{groupName},
        #{loginEmpSeq}
    )

mapper.update:
/* setGroupMod */
UPDATE
    CAM_HR.DJ_TEXTMESSAGES_GROUP
SET
    GROUP_NAME = #{groupName}
WHERE
    GROUP_ID = #{groupId}

mapper.delete:
/* setGroupDel */
DELETE FROM
    CAM_HR.DJ_TEXTMESSAGES_GROUP
WHERE
    GROUP_Id = #{groupId}

mapper.update:
/* setUserMod */
UPDATE CAM_HR.DJ_TEXTMESSAGES_USER
SET
    PHONE_USER_NAME = #{phoneNameUser},
    PHONE_USER_NUM = #{phoneUserNum},
    GROUP_ID = #{userGroupId}
WHERE
    PHONE_USER_ID = #{phoneUserId}

mapper.delete:
/* setUserDel */
DELETE FROM
    CAM_HR.DJ_TEXTMESSAGES_USER
WHERE
    PHONE_USER_ID = #{phoneUserId}

mapper.update:
/* setMailHistUpd */
UPDATE CAM_COMMON.DJ_MAIL_HIST
SET
    MAIL_TILE = #{subject},
    MAIL_CONTENT = #{contents},
    SEND_DATE = #{sendDate},
    SEND_EMAIL = #{sendEml}
WHERE
    MAIL_HIST_SN = #{mailHistSn}

mapper.insert:
/* setMailDetIns */
INSERT INTO CAM_COMMON.DJ_MAIL_HIST_DET
    (
        MAIL_HIST_SN,
        NAME,
        EMAIL
    )
VALUES
    (
        #{mailHistSn},
        #{name},
        #{email}
    )

mapper.update:
/* setMailDetCom */ UPDATE CAM_COMMON.DJ_MAIL_HIST_DET SET SEND_YN = 'Y', SEND_DATE = DATE_FORMAT(NOW(), '%Y-%m-%d') WHERE MAIL_HIST_SN = #{mailHistSn}
AND MAIL_HIST_DET_SN IN (${mailArr})
AND SEND_YN = 'N' AND ACTIVE = 'Y'

mapper.delete:
/* setMailDetDel */
DELETE FROM CAM_COMMON.DJ_MAIL_HIST_DET
WHERE
    MAIL_HIST_DET_SN = #{mailHistDetSn}
