mapper.insert:
/* setGroup */
INSERT INTO CAM_HR.DJ_TEXTMESSAGES_GROUP
    (
        GROUP_NAME,
        REG_EMP_SEQ
    )
VALUES
    (
        #{groupName},
        #{loginEmpSeq}
    )

mapper.update:
/* setGroupMod */
UPDATE
    CAM_HR.DJ_TEXTMESSAGES_GROUP
SET
    GROUP_NAME = #{groupName}
WHERE
    GROUP_ID = #{groupId}

mapper.delete:
/* setGroupDel */
DELETE FROM
    CAM_HR.DJ_TEXTMESSAGES_GROUP
WHERE
    GROUP_Id = #{groupId}

mapper.update:
/* setUserMod */
UPDATE CAM_HR.DJ_TEXTMESSAGES_USER
SET
    PHONE_USER_NAME = #{phoneNameUser},
    PHONE_USER_NUM = #{phoneUserNum},
    GROUP_ID = #{userGroupId}
WHERE
    PHONE_USER_ID = #{phoneUserId}

mapper.delete:
/* setUserDel */
DELETE FROM
    CAM_HR.DJ_TEXTMESSAGES_USER
WHERE
    PHONE_USER_ID = #{phoneUserId}
