mapper.select:
/*getMessageHistList*/ SELECT * FROM ( SELECT 'SMS' AS TYPE, SUBJECT, SMS_MSG AS MSG, SUBSTRING(SEND_DATE, 1, 4) + '-' + SUBSTRING(SEND_DATE, 5, 2) + '-' + SUBSTRING(SEND_DATE, 7, 2) AS SEND_DATE, SEND_DATE AS SEND_DT, CALLBACK, DEST_INFO FROM CAM_TOOLS.SDK_SMS_REPORT
UNION ALL
SELECT 'MMS' AS TYPE, SUBJECT, MMS_MSG AS MSG, SUBSTRING(SEND_DATE, 1, 4) + '-' + SUBSTRING(SEND_DATE, 5, 2) + '-' + SUBSTRING(SEND_DATE, 7, 2) AS SEND_DATE, SEND_DATE AS SEND_DT, CALLBACK, DEST_INFO FROM CAM_TOOLS.SDK_MMS_REPORT ) RE WHERE RE.SEND_DATE BETWEEN #{strDe} AND #{endDe}
AND RE.TYPE = #{searchType}
AND RE.SUBJECT LIKE CONCAT('%', #{searchValue}, '%')
AND RE.MSG LIKE ('%', #{searchValue}, '%')
AND ( RE.SUBJECT LIKE ('%', #{searchValue}, '%') OR RE.MSG LIKE ('%', #{searchValue}, '%') )
ORDER BY RE.SEND_DT DESC

mapper.insert:
/*msgSendSMS*/ INSERT INTO CAM_TOOLS.SDK_SMS_SEND ( USER_ID, SCHEDULE_TYPE, SUBJECT, NOW_DATE, SEND_DATE,
CALLBACK, DEST_COUNT, DEST_INFO, SMS_MSG ) VALUES ( 'camtic', '0', 'CAMTIC SMS 서비스', #{pkDate}, #{pkDate},
#{callBack}, '1', #{dest_phone}, #{msg_content} )

/*msgSendFMS*/ INSERT INTO CAM_TOOLS.SDK_FMS_SEND ( USER_ID, MSG_SUBTYPE, SCHEDULE_TYPE, SUBJECT, NOW_DATE, SEND_DATE,
CALLBACK, DEST_COUNT, DEST_INFO, ATTACH_FILE ) VALUES ( 'camtic', '20', '0', 'CAMTIC FMS 서비스', #{pkDate}, #{pkDate},
#{requestNum}, '1', #{dest_phone}, #{fileUUID} )
