mapper.select:
/* getOrgDeptList */ SELECT EMP_NAME_KR, DUTY_NAME, BDAY
FROM DJ_CAMTIC.V_EMP_INFO VEI
WHERE VEI.ACTIVE = 'Y' and VEI.DEPT_NAME = #{deptNm}

/* getUserInfo */ SELECT VEI.EMP_SEQ, VEI.EMP_NAME_KR, VEI.LOGIN_ID, VEI.DEPT_SEQ, VEI.DEPT_NAME, VEI.POSITION_CODE, VEI.POSITION_CODE AS POSITION_NAME, VEI.DUTY_CODE, VEI.DUTY_NAME FROM DJ_CAMTIC.V_EMP_INFO VEI WHERE 1=1
AND VEI.ACTIVE = 'Y'
AND VEI.EMP_SEQ = #{empSeq}
