mapper.select:
/* getOrgDeptList */ SELECT EMP_NAME_KR, DUTY_NAME, BDAY
FROM CAM_HR.DJ_EMP_INFO VEI
WHERE VEI.ACTIVE = 'Y' and VEI.DEPT_NAME = #{deptNm}

/* getUserInfo */ SELECT HEI.EMP_SEQ, HEI.ERP_EMP_SEQ, HEI.EMP_NAME_KR, HEI.LOGIN_ID, HEI.DEPT_SEQ, CASE WHEN HDI.DEPT_LEVEL = 2 THEN (SELECT DEPT_NAME FROM CAM_HR.DJ_DEPT_INFO DI WHERE HDI.DEPT_SEQ = DI.DEPT_SEQ) ELSE HEI.DEPT_NAME END AS DEPT_NAME, CASE WHEN HDI.DEPT_LEVEL = 2 THEN (SELECT DEPT_SEQ FROM CAM_HR.DJ_DEPT_INFO DI WHERE HDI.PARENT_DEPT_SEQ = DI.DEPT_SEQ) ELSE HEI.DEPT_SEQ END AS deptId, CASE WHEN HDI.DEPT_LEVEL = 2 THEN (SELECT DEPT_NAME FROM CAM_HR.DJ_DEPT_INFO DI WHERE HDI.PARENT_DEPT_SEQ = DI.DEPT_SEQ) ELSE HEI.DEPT_NAME END AS deptNm, CASE WHEN HDI.DEPT_LEVEL = 2 THEN HEI.DEPT_SEQ ELSE "" END AS teamId, CASE WHEN HDI.DEPT_LEVEL = 2 THEN (SELECT DEPT_NAME FROM CAM_HR.DJ_DEPT_INFO DI WHERE HDI.DEPT_SEQ = DI.DEPT_SEQ) ELSE "" END AS teamNm, HEI.POSITION_CODE, IFNULL(HEI.POSITION_NAME, '') AS POSITION_NAME, HEI.DUTY_CODE, IFNULL(HEI.DUTY_NAME, '') AS DUTY_NAME, HEI.OFFICE_TEL_NUM, HEI.EMAIL_ADDR, HEI.MOBILE_TEL_NUM, HEI.HOME_TEL_NUM, HDI.DEPT_LEVEL, HEI.DIVISION, HEI.DIVISION_SUB, HEI.JOB_DETAIL FROM CAM_HR.DJ_EMP_INFO HEI LEFT JOIN CAM_HR.DJ_DEPT_INFO HDI ON HEI.DEPT_SEQ = HDI.DEPT_SEQ WHERE 1=1
AND HEI.ACTIVE = 'Y'
AND HEI.EMP_SEQ = #{empSeq}

/* getEmpList */ SELECT HEI.EMP_SEQ, CASE WHEN HDI.DEPT_LEVEL = 2 THEN (SELECT DEPT_NAME FROM CAM_HR.DJ_DEPT_INFO DI WHERE HDI.PARENT_DEPT_SEQ = DI.DEPT_SEQ) ELSE HEI.DEPT_NAME END AS DEPT_NAME, CASE WHEN HDI.DEPT_LEVEL = 2 THEN HEI.DEPT_NAME ELSE "" END AS TEAM_NAME, HEI.EMP_NAME_KR FROM CAM_HR.DJ_EMP_INFO HEI LEFT JOIN CAM_HR.DJ_DEPT_INFO HDI ON HEI.DEPT_SEQ = HDI.DEPT_SEQ WHERE ACTIVE = 'Y'
AND HEI.DEPT_SEQ IN ( SELECT HDI.DEPT_SEQ FROM CAM_HR.DJ_DEPT_INFO HDI WHERE HDI.DEPT_SEQ = #{deptSeq} OR HDI.PARENT_DEPT_SEQ = #{deptSeq} )
AND HEI.EMP_NAME_KR LiKE CONCAT ('%', #{empName}, '%')
AND HEI.WORK_STATUS_CODE = #{workStatusCode}
