mapper.select:
/* getOrgDeptList */ SELECT EMP_NAME_KR, DUTY_NAME, scp.dec_b64('KEY1', BDAY) AS BDAY
FROM CAM_HR.DJ_EMP_INFO VEI
WHERE VEI.ACTIVE = 'Y' and VEI.DEPT_NAME = #{deptNm}

/* getUserInfo */ SELECT HEI.EMP_SEQ, HEI.ERP_EMP_SEQ, HEI.EMP_NAME_KR, HEI.LOGIN_ID, HEI.DEPT_SEQ, CASE WHEN HDI.DEPT_LEVEL = 2 THEN (SELECT DEPT_NAME FROM CAM_HR.DJ_DEPT_INFO DI WHERE HDI.DEPT_SEQ = DI.DEPT_SEQ) ELSE HEI.DEPT_NAME END AS DEPT_NAME, CASE WHEN HDI.DEPT_LEVEL = 2 THEN (SELECT DEPT_SEQ FROM CAM_HR.DJ_DEPT_INFO DI WHERE HDI.PARENT_DEPT_SEQ = DI.DEPT_SEQ) ELSE HEI.DEPT_SEQ END AS deptId, CASE WHEN HDI.DEPT_LEVEL = 2 THEN (SELECT DEPT_NAME FROM CAM_HR.DJ_DEPT_INFO DI WHERE HDI.PARENT_DEPT_SEQ = DI.DEPT_SEQ) ELSE HEI.DEPT_NAME END AS deptNm, CASE WHEN HDI.DEPT_LEVEL = 2 THEN HEI.DEPT_SEQ ELSE "" END AS teamId, CASE WHEN HDI.DEPT_LEVEL = 2 THEN (SELECT DEPT_NAME FROM CAM_HR.DJ_DEPT_INFO DI WHERE HDI.DEPT_SEQ = DI.DEPT_SEQ) ELSE "" END AS teamNm, HEI.POSITION_CODE, IFNULL(HEI.POSITION_NAME, '') AS POSITION_NAME, HEI.DUTY_CODE, IFNULL(HEI.DUTY_NAME, '') AS DUTY_NAME, HEI.OFFICE_TEL_NUM, scp.dec_b64('KEY1', HEI.EMAIL_ADDR) AS EMAIL_ADDR, scp.dec_b64('KEY1', HEI.MOBILE_TEL_NUM) AS MOBILE_TEL_NUM, HEI.HOME_TEL_NUM, HDI.DEPT_LEVEL, HEI.DIVISION, HEI.DIVISION_SUB, HEI.JOB_DETAIL, IM.FILE_PATH, scp.dec_b64('KEY1', HEI.BDAY) AS BDAY, DATE_FORMAT(HEI.JOIN_DAY,'%Y-%m-%d') AS JOIN_DAY,
CASE WHEN CC.CM_CODE_NM = 'P&M' THEN CONCAT('경영관리(', CC.CM_CODE_NM,')') WHEN CC.CM_CODE_NM = 'A&C' THEN CONCAT('기업지원(', CC.CM_CODE_NM,')') WHEN CC.CM_CODE_NM = 'R&D' THEN CONCAT('연구개발(', CC.CM_CODE_NM,')') ELSE '' END AS OCC_NM, CC.CM_CODE_NM AS OCCUPATION_NM
FROM CAM_HR.DJ_EMP_INFO HEI LEFT JOIN CAM_HR.DJ_DEPT_INFO HDI ON HEI.DEPT_SEQ = HDI.DEPT_SEQ LEFT JOIN ( SELECT IM.SIGN_IMAGE_PK, CONCAT(FI.FILE_PATH, FI.FILE_UUID) AS FILE_PATH, IM.EMP_SEQ FROM CAM_INSIDE.DJ_IMAGE_MANAGE IM LEFT JOIN CAM_COMMON.DJ_FILE_INFO FI ON FI.FILE_NO = IM.SIGN_IMAGE_PK )IM ON IM.EMP_SEQ = HEI.EMP_SEQ LEFT JOIN CAM_COMMON.DJ_COM_CODE CC ON CC.CM_GROUP_CODE_ID = 9 AND HEI.OCCUPATION_CODE = CC.CM_CODE WHERE 1=1
AND HEI.ACTIVE = 'Y'
AND HEI.EMP_SEQ = #{empSeq}

/* getEmpList */ SELECT HEI.EMP_SEQ, CASE WHEN HDI.DEPT_LEVEL = 2 THEN (SELECT DEPT_NAME FROM CAM_HR.DJ_DEPT_INFO DI WHERE HDI.PARENT_DEPT_SEQ = DI.DEPT_SEQ) ELSE HEI.DEPT_NAME END AS DEPT_NAME, CASE WHEN HDI.DEPT_LEVEL = 2 THEN HEI.DEPT_NAME ELSE "" END AS TEAM_NAME, HEI.EMP_NAME_KR FROM CAM_HR.DJ_EMP_INFO HEI LEFT JOIN CAM_HR.DJ_DEPT_INFO HDI ON HEI.DEPT_SEQ = HDI.DEPT_SEQ WHERE ACTIVE = 'Y'
AND HEI.DEPT_SEQ IN ( SELECT HDI.DEPT_SEQ FROM CAM_HR.DJ_DEPT_INFO HDI WHERE HDI.DEPT_SEQ = #{deptSeq} OR HDI.PARENT_DEPT_SEQ = #{deptSeq} )
AND HEI.EMP_NAME_KR LiKE CONCAT ('%', #{empName}, '%')
AND HEI.WORK_STATUS_CODE = #{workStatusCode}
