mapper.select:
/* getUserPersonnelRecordList */ SELECT
(CASE WHEN CAST(SUBSTRING_INDEX(a.BEFOR_CAREER/12, '.', 1) AS SIGNED) >= 1 THEN CAST(SUBSTRING_INDEX(a.BEFOR_CAREER/12, '.', 1) AS SIGNED) ELSE 0 END) AS prev_hire, (CASE WHEN CAST(SUBSTRING_INDEX(a.BEFOR_CAREER/12, '.', 1) AS SIGNED) >= 1 THEN CONCAT(CAST(a.BEFOR_CAREER AS SIGNED) - (CAST(SUBSTRING_INDEX(a.BEFOR_CAREER/12, '.', 1) AS SIGNED) * 12)) ELSE a.BEFOR_CAREER END) AS prev_hire_mon,
SUBSTRING_INDEX(TRUNCATE(TIMESTAMPDIFF(month, a.JOIN_DAY, DATE_FORMAT(NOW(), '%Y-%m-%d')) / 12, 1), '.', 1) AS hire, CONCAT(SUBSTRING_INDEX(TRUNCATE(TIMESTAMPDIFF(month, a.JOIN_DAY, DATE_FORMAT(NOW(), '%Y-%m-%d')) / 12, 1), '.', -1) + 1) AS hire_mon, a.emp_seq AS 'empSeq', a.erp_emp_seq AS 'erpEmpSeq', a.login_id AS 'loginId', a.EMP_NAME_KR AS 'empName', a.EMP_NAME_CN AS 'empNameCn', a.BDAY as 'bDay', a.ZIP_CODE AS 'zipCode', a.ADDR AS 'addr', a.OLD_ADDR AS 'oldAddr', a.ADDR_DETAIL AS 'addrDetail', a.ADDR_REFERENCES AS 'addrReferences', a.OFFICE_TEL_NUM AS 'officeTelNum', a.MOBILE_TEL_NUM AS 'mobileTelNum', a.EMG_TEL_NUM as 'emgTelNum', a.CAR_ACTIVE AS 'carActive', a.CAR_NUM AS 'carNum', a.JOIN_DAY AS 'joinDay', a.RESIGN_DAY AS 'resignDay', a.RES_REGIS_NUM AS 'resRegisNum', a.BANK_NAME AS 'BANK_NAME', a.ACCOUNT_NUM AS 'ACCOUNT_NUM', a.DEPT_SEQ AS 'deptSeq', a.DEPT_NAME AS 'deptName', a.DEPT_TEAM_NAME AS 'teamName', a.CARD_ETC AS 'cardEtc' FROM cam_hr.dj_emp_info AS a WHERE a.EMP_SEQ = #{empSeq};

/* getAppointInfoList */
SELECT
    A.*,
    DATE_FORMAT(HISTORY_DT, '%Y-%m-%d') AS historyDt
FROM
    CAM_HR.DJ_APNT_INFO A
WHERE
    A.EMP_SEQ = #{empSeq}
ORDER BY A.HISTORY_DT DESC;

/** getEmpInfoList */ SELECT HEI.*, CASE WHEN HDI.DEPT_LEVEL = 2 THEN (SELECT DEPT_SEQ FROM CAM_HR.DJ_DEPT_INFO DI WHERE HDI.PARENT_DEPT_SEQ = DI.DEPT_SEQ) ELSE HEI.DEPT_SEQ END AS DEPT_SEQ, CASE WHEN HDI.DEPT_LEVEL = 2 THEN (SELECT DEPT_NAME FROM CAM_HR.DJ_DEPT_INFO DI WHERE HDI.PARENT_DEPT_SEQ = DI.DEPT_SEQ) ELSE HEI.DEPT_NAME END AS DEPT_NAME1, CASE WHEN HDI.DEPT_LEVEL = 2 THEN HEI.DEPT_SEQ ELSE "" END AS TEAM_SEQ, CASE WHEN HDI.DEPT_LEVEL = 2 THEN HEI.DEPT_NAME ELSE "" END AS TEAM_NAME, DATE_FORMAT(HEI.JOIN_DAY,'%Y-%m-%d') AS JOIN_DAY2, IFNULL(CASE WHEN HEI.DUTY_NAME = "" THEN HEI.POSITION_NAME ELSE HEI.DUTY_NAME END, "") AS SPOT,
CASE WHEN HEI.DUTY_CODE IS NOT NULL THEN (SELECT SORT FROM CAM_COMMON.dj_com_code WHERE CM_GROUP_CODE_ID = 3 AND CM_CODE = HEI.DUTY_CODE) WHEN HEI.POSITION_CODE IS NOT NULL THEN (SELECT SORT FROM CAM_COMMON.dj_com_code WHERE CM_GROUP_CODE_ID = 4 AND CM_CODE = HEI.POSITION_CODE) ELSE 999 END SORT
FROM CAM_HR.DJ_EMP_INFO AS HEI LEFT JOIN CAM_HR.DJ_DEPT_INFO HDI ON HEI.DEPT_SEQ = HDI.DEPT_SEQ WHERE HEI.ACTIVE = 'Y'
AND HEI.GENDER_CODE = #{userGender}
AND DATE_FORMAT(#{startDate}, '%Y%m%d') >= DATE_FORMAT(HEI.JOIN_DAY, '%Y%m%d')
AND ${userKind} like concat('%', #{kindContent} ,'%')
AND HEI.DEPT_SEQ = #{deptComp}
OR HEI.DEPT_SEQ IN ( SELECT DEPT_SEQ FROM CAM_HR.DJ_DEPT_INFO WHERE DEPT_SEQ = #{deptComp} OR PARENT_DEPT_SEQ = #{deptComp} )
AND HEI.GENDER_CODE = #{userGender}
AND HEI.DEPT_SEQ = #{deptTeam}
AND
${item}
AND TEMP_DIVISION = "Y" AND WORK_STATUS_CODE = "Y"
AND TEMP_DIVISION = "N"
ORDER BY RESIGN_DAY DESC
ORDER BY EMP_NAME_KR

