mapper.update:
/* setTmpDutyDel */
UPDATE
    CAM_HR.DJ_EMP_TMP_DUTY
SET
    ACTIVE = 'N'
    , MOD_DT = NOW()
    , MOD_EMP_SEQ = #{regEmpSeq}
WHERE
    EMP_TMP_SN = #{empTmpSn}

mapper.select:
/*getTmpDutyList*/ SELECT ETD.EMP_TMP_SN, CASE WHEN DI.DEPT_LEVEL = '2' THEN CONCAT((SELECT IFNULL(DEPT_NAME, '') FROM CAM_HR.DJ_DEPT_INFO SDI WHERE DEPT_SEQ = DI.PARENT_DEPT_SEQ), ' ', DI.DEPT_NAME) ELSE DI.DEPT_NAME END AS TMP_DEPT_NAME, ETD.TMP_DUTY_NAME FROM CAM_HR.DJ_EMP_TMP_DUTY ETD LEFT JOIN CAM_HR.DJ_DEPT_INFO DI ON ETD.TMP_DEPT_SEQ = DI.DEPT_SEQ WHERE ACTIVE = 'Y'
AND EMP_SEQ = #{empSeq}
ORDER BY EMP_TMP_SN DESC

/*getAllDutyList*/ SELECT DI.DEPT_SEQ, DI.DEPT_NAME FROM CAM_HR.DJ_EMP_INFO EI LEFT JOIN CAM_HR.DJ_DEPT_INFO DI ON EI.DEPT_SEQ = DI.DEPT_SEQ WHERE EI.EMP_SEQ = #{empSeq}
UNION ALL
SELECT ETD.TMP_DEPT_SEQ, DI.DEPT_NAME AS TMP_DEPT_NAME FROM CAM_HR.DJ_EMP_TMP_DUTY ETD LEFT JOIN CAM_HR.DJ_DEPT_INFO DI ON ETD.TMP_DEPT_SEQ = DI.DEPT_SEQ WHERE ACTIVE = 'Y' AND EMP_SEQ = #{empSeq}
