mapper.select:
SELECT a.*, date_format(a.JOIN_DAY,'%Y-%m-%d') AS JOIN_DAY2 FROM CAM_HR.DJ_EMP_INFO AS a WHERE a.active = 'Y'
AND DATE_FORMAT(#{startDate}, '%Y%m%d') >= DATE_FORMAT(a.JOIN_DAY, '%Y%m%d')
AND ${userKind} like concat('%', #{kindContent} ,'%')
AND a.DEPT_SEQ = #{deptComp}
OR a.DEPT_SEQ IN ( SELECT DEPT_SEQ FROM CAM_HR.DJ_DEPT_INFO WHERE DEPT_SEQ = #{deptComp} OR PARENT_DEPT_SEQ = #{deptComp} )
AND a.DEPT_SEQ = #{deptTeam}
AND DIVISION IN
#{item}

mapper.insert:
INSERT INTO cam_hr.dj_emp_duty_info(
        EMP_SEQ
        , EMP_NAME
        , POSITON_NAME
        , WORK_PAY
        , DUTY_DETAIL
        , WORK_JOIN_DAY
        , WORK_LEAVE_DAY
        , REG_EMP_SEQ
        , REG_DATE
)VALUES (
        #{EMP_SEQ}
        , #{EMP_NAME}
        , #{POSITON_NAME}
        , #{WORK_PAY}
        , #{DUTY_DETAIL}
        , #{sDate}
        , #{eDate}
        , #{EMP_SEQ}
        , NOW()
)

mapper.update:
UPDATE cam_hr.${TYPE}
SET
    ADMIN_APPROVAL = 'Y',
    ADMIN_APPROVAL_DATE = NOW()
WHERE
    EMP_SEQ = #{EMP_SEQ}
    and
    ${KEY} = #{ID}

UPDATE cam_hr.${TYPE}
SET
    ADMIN_APPROVAL = 'N',
    ADMIN_APPROVAL_DATE = NOW()
WHERE
    EMP_SEQ = #{EMP_SEQ}
  and
    ${KEY} = #{ID}

UPDATE cam_hr.${TYPE}
SET
    RETURNYN = 'Y',
    RETURNYN_DATE = NOW()
WHERE
    EMP_SEQ = #{EMP_SEQ}
    and
    ${KEY} = #{ID}

UPDATE cam_hr.${TYPE}
SET
    RETURNYN = 'N',
    RETURNYN_DATE = NOW()
WHERE
    EMP_SEQ = #{EMP_SEQ}
  and
    ${KEY} = #{ID}
