mapper.update:
UPDATE cam_hr.${TYPE}
SET
    ADMIN_APPROVAL = 'Y',
    ADMIN_APPROVAL_DATE = NOW()
WHERE
    EMP_SEQ = #{EMP_SEQ}
    and
    ${KEY} = #{ID}

UPDATE cam_hr.${TYPE}
SET
    ADMIN_APPROVAL = 'N',
    ADMIN_APPROVAL_DATE = NOW()
WHERE
    EMP_SEQ = #{EMP_SEQ}
  and
    ${KEY} = #{ID}
